// Perform a separable scharr filter in X
float scharr_x(in float texels[9])
{ 
    const float weights[9] = float[](
         -3.0,  0.0,   3.0,
        -10.0,  0.0,  10.0,
         -3.0,  0.0,   3.0
    );

    float filtered = 0.0;

    for (int y = 0; y < 3; y++)
    {
        for (int x = 0; x < 3; x++)
        {
            int idx = y * 3 + x;
            filtered += weights[idx] * texels[idx];
        }
    }
    return filtered;
}

// Perform a separable scharr filter in Y
float scharr_y(in float texels[9])
{
    const float weights[9] = float[](
          3.0,  10.0,  3.0,
          0.0,   0.0,  0.0,
         -3.0, -10.0, -3.0
    );

    float filtered = 0.0;

    for (int y = 0; y < 3; y++)
    {
        for (int x = 0; x < 3; x++)
        {
            int idx = y * 3 + x;
            filtered += weights[idx] * texels[idx];
        }
    }
    return filtered;
}

/*
// Scharr Filter
//
// This is a specialisation of the 3x3 Sobel edge detection filter where
// the filter kernel produces a nice rotationally consistent result.
//
// This implementation first samples the pool of 9 texels we need for the
// filter, then runs our x and y separable filter kernels over them, 
// and combining them to give us our final gradient value
*/
#define USE_TEXEL_FETCH
#if defined(USE_TEXEL_FETCH)
float scharr_filter_3x3(in ivec2 fragCoord)
{
    float texels[9];

    // Little heuristic that seems to make the sampling behaviour consistent-ish
    // across different screen resolutions. Originally I used texelFetch as I was
    // just applying the filter at the source resolution, but when you stretch the
    // image across the screen you change the response of the filter. Might be
    // better to switch back to normal image sampling.
    float offset = max(iResolution.x, iResolution.y) / 400.0;
    offset = sqrt(offset);

    texels[0] = (texelFetch(iChannel0, fragCoord + ivec2(-offset, -offset), 0).a);
    texels[1] = (texelFetch(iChannel0, fragCoord + ivec2(      0, -offset), 0).a);
    texels[2] = (texelFetch(iChannel0, fragCoord + ivec2( offset, -offset), 0).a);

    texels[3] = (texelFetch(iChannel0, fragCoord + ivec2(-offset,  0), 0).a);
    texels[4] = (texelFetch(iChannel0, fragCoord + ivec2(      0,  0), 0).a);
    texels[5] = (texelFetch(iChannel0, fragCoord + ivec2( offset,  0), 0).a);

    texels[6] = (texelFetch(iChannel0, fragCoord + ivec2(-offset,  offset), 0).a);
    texels[7] = (texelFetch(iChannel0, fragCoord + ivec2(      0,  offset), 0).a);
    texels[8] = (texelFetch(iChannel0, fragCoord + ivec2( offset,  offset), 0).a);

    float x = scharr_x(texels);
    float y = scharr_y(texels);

    //return abs(x) + abs(y);
    return sqrt(x*x + y*y);
}
#else
float scharr_filter_3x3(in ivec2 fragCoord)
{
    float texels[9];

    // Little heuristic that seems to make the sampling behaviour consistent-ish
    // across different screen resolutions. Originally I used texelFetch as I was
    // just applying the filter at the source resolution, but when you stretch the
    // image across the screen you change the response of the filter. Might be
    // better to switch back to normal image sampling.
    float delta = sqrt(max(iResolution.x, iResolution.y) / 400.0);
    delta = sqrt(delta);

    vec2 offset = vec2(delta);
    vec2 uv = vec2(fragCoord) / iResolution.xy;
    offset /= iResolution.xy;

    texels[0] = (texture(iChannel0, uv + vec2(-offset.x, -offset.y)).a);
    texels[1] = (texture(iChannel0, uv + vec2(        0, -offset.y)).a);
    texels[2] = (texture(iChannel0, uv + vec2( offset.x, -offset.y)).a);

    texels[3] = (texture(iChannel0, uv + vec2(-offset.x,  0)).a);
    texels[4] = (texture(iChannel0, uv + vec2(        0,  0)).a);
    texels[5] = (texture(iChannel0, uv + vec2( offset.x,  0)).a);

    texels[6] = (texture(iChannel0, uv + vec2(-offset.x,  offset.y)).a);
    texels[7] = (texture(iChannel0, uv + vec2(        0,  offset.y)).a);
    texels[8] = (texture(iChannel0, uv + vec2( offset.x,  offset.y)).a);

    float x = scharr_x(texels);
    float y = scharr_y(texels);

    //return abs(x) + abs(y);
    return sqrt(x*x + y*y);
}
#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scharr = scharr_filter_3x3(ivec2(fragCoord));  
    vec4 original = texelFetch(iChannel0, ivec2(fragCoord), 0).rgba;

    // Store the original image, and the scharr filter result
    fragColor = vec4(original.rgb, scharr);
    //fragColor = vec4(original);
}

