////////////////////////////////////////////////////////////////////////////////
//
// Sliding Tiles II, mla 2022
//
// Tiles sliding in spiral paths in two directions on a sphere. Default view
// is from just inside the sphere, so is a stereographic projection. Alternative
// views are from the centre and from an external location.
//
// <mouse>: rotate view
// 'a': MSAA
// 'g': show grid
// 'h': grid thickness
// 'f': show flat tiling
// 'r': autorotation
// 'c': show centre view
// 'e': show external view
// 'l': London view
// 's': alternate tile orientation
// 't': tile coloring
//
////////////////////////////////////////////////////////////////////////////////

ivec2 getindex(int t, vec2 uv) {
  assert(t >= 0);
  // Calculate which tile appears at point uv at time t.
  // Other patterns should be possible.
  ivec2 ix = ivec2(floor(uv));
  if (odd(ix.x) && odd(ix.y)) return ix; // Stationary tiles
  ix -= t/6*2; // Tiles move 2 every 6 cycles
  t %= 6;
  for ( ; t != 0; t--) {
    // A neat recurrence. Maybe this could be simplified.
    if (even(t)) {
      if (even(ix.x)) ix.y--;
    } else {
      if (even(ix.y)) ix.x--;
    }
  }
  return ix;
}

vec3 slider(vec2 uv, mat2 dxdy) {
  //uv.x = mod(uv.x+1.0,2.0)-1.0; // Sanity check - should have no effect
  //uv *= 3.0; // Zoom out
  float theta = -PI/4.0;
  uv = rotate(uv,theta);
  uv *= sqrt(2.0), dxdy *= sqrt(2.0);
  int N = 5;
  float scale = float(N);
  uv *= scale; dxdy *= scale;
  float t = 0.5*max(0.0,iTime-1.0); // 1 second delay before moving
  int cycle = int(t);
  t = smoothstep(0.0,1.0,fract(t));
  // Move those tiles
  if (even(cycle)) {
    if (mod(uv.y,2.0) < 1.0) {
      uv.x -= t;
    }
  } else {
    if (mod(uv.x,2.0) < 1.0) {
      uv.y -= t;
    }
  }
  ivec2 ix = getindex(cycle, uv);
  ix -= N; // Image boundary to 0,0
  // This has the effect of horizontal periodicity, but not vertical. 
  int k = idiv(ix.x,10);
  ix.x -= 2*N*k; //
  ix.y += 2*N*k;
  ix += N; // Image centre to 0,0

  // Now fetch the texture
  float tscale = 0.5/scale;
  vec2 tuv = tscale*(vec2(ix)+fract(uv));
  tuv += 0.5;
  vec3 col = vec3(1);
  if (!key(CHAR_S) && ((ix.x+ix.y)&1) == 1) tuv = tuv.yx;

  vec2 dx = tscale*dxdy[0], dy = tscale*dxdy[1];
  col = key(CHAR_L) ? textureGrad(iChannel0,tuv,dx,dy).rgb :
                      textureGrad(iChannel1,tuv,dx,dy).rgb;
  //col = texture(iChannel0,tuv).rgb; // See those artefacts!
  col = pow(col,vec3(2.2));
  col = 1.2*col;
  uint h = ihash(uint(ix.y*1024+ix.x));
  if (!key(CHAR_T)) col *= 0.2+0.8*h2rgb(float(h)/exp2(32.0));
  if (key(CHAR_G)) {
    // Draw grid
    float px = length(dxdy[0]); // ?!
    uv = fract(uv);
    uv = min(uv,1.0-uv);
    float d = min(uv.x,uv.y);
    float gwidth = !key(CHAR_H) ? 0.0 : 0.015;
    col = mix(vec3(0),col,smoothstep(0.0,px,d-gwidth));
  }
  col = 2.0*col/(1.0+col);
  return col;
}

void mainImage0( out vec4 fragColor, in vec2 fragCoord ) {
  // Flat image
  vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
  vec3 col = slider(uv,mat2(dFdx(uv),dFdy(uv)));
  col = pow(col,vec3(0.4545));
  if (abs(uv.x) > 1.0) col *= 0.8; //Show central region
  if (alert) col.r = 1.0;
  fragColor = vec4(col,1.0);
}

vec3 getColor(vec3 p) {
  float x = atan(p.x,p.z);       // Longitude
  float y = asin(p.y/length(p)); // Latitude
  y = log(tan(0.25*PI+0.5*y));   // Mercator
  vec2 uv = vec2(x,y)/PI;
  // mipmap shenanigans, each of dxdy, dxdy1 have
  // discontinuities, but at different places,
  // so choose the best one.
  vec2 uv1 = vec2(mod(uv.x,2.0),uv.y);
  mat2 dxdy = mat2(dFdx(uv),dFdy(uv));
  mat2 dxdy1 = mat2(dFdx(uv1),dFdy(uv1));
  if (abs(uv.x) > 0.5) dxdy = dxdy1;
  float scale = 2.0;
  return slider(scale*uv,scale*dxdy);
}

struct Ray {
  vec3 q;               // origin
  vec3 d;               // direction
};

struct Hit {
  float t;      // solution to p=q+t*d
  vec3 n;       // normal
};

struct Sphere {
  float r;      // radius
  vec3 p;       // centre
};

bool intersectSphere(Sphere s, Ray ray, out Hit hit) {
  vec3 p = s.p;
  float r = s.r;
  float c = length(p);
  vec3 q = ray.q, d = ray.d;
  // |q + t*d - p|^2 = r^2
  float B = dot(q-p,d);
  float C = dot(q,q)-2.0*dot(q,p)+(c+r)*(c-r);
  float D = B*B - C;
  if (D < 0.0) return false;
  D = sqrt(D);
  float t,t1;
  if (B >= 0.0) {
    t = -B-D; t1 = C/t;
  } else {
    t1 = -B+D; t = C/t1;
  }
  if (t <= 0.001) t = t1;
  if (t <= 0.001) return false;
  // Normal is the radial vector of sphere
  hit = Hit(t, (q+t*d-p)/r);
  return true;
}

bool intersectScene(Ray r, out Hit hit) {
  Sphere s = Sphere(1.0, vec3(0));
  if (intersectSphere(s,r,hit)) {
    return true;
  }
  return false;
}

vec3 light;
float ambient;
float diffuse;

vec3 solve(Ray r) {
  Hit hit;
  if (!intersectScene(r,hit)) {
    return vec3(0);
  } else {
    vec3 n = hit.n;
    if (dot(r.d,n) > 0.0) n = -n;
    vec3 basecolor = getColor(hit.n);
    vec3 color = basecolor.xyz*(ambient+diffuse*max(0.0,dot(light,n)));
    float specular = pow(max(0.0,dot(reflect(light,n),r.d)),10.0);
    vec3 speccolor = vec3(1);
    color += 0.25*specular*speccolor;
    color *= clamp(1.0 - (hit.t-3.0)/5.0,0.0,1.0);
    return color;
  }
}

vec3 transform0(vec3 p) {
  if (!key(CHAR_R)) {
    p.yz = rotate(p.yz,iTime * 0.125);
    p.zx = rotate(p.zx,iTime * 0.2);
  }
  return p;
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = -(2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = -(2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  return transform0(p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  if (key(CHAR_F)) { mainImage0(fragColor,fragCoord); return; }
  float camera = 1.0, fov = 1.0;
  if (key(CHAR_C)) {
    camera = 0.0;
  } else if (key(CHAR_E)) {
    camera = 3.0;
    fov = 2.0;
  }
  vec3 p = vec3(0,0,-camera);
  p = transform(p);
  light = vec3(0.25,camera > 1.0 ? 1.0 : -1.0,-0.25);
  light = transform0(light);
  light = normalize(light);
  ambient = 0.5;
  diffuse = 1.0-ambient;
  vec3 col = vec3(0);
  float AA = 1.0;
  if (key(CHAR_A)) AA = 2.0;
  for (float i = 0.0; i < AA; i++) {
    for (float j = 0.0; j < AA; j++) {
      vec2 uv = (2.0*(fragCoord+vec2(i,j)/AA)-iResolution.xy)/iResolution.y;
      vec3 r = vec3(uv,fov);
      r = transform(r);
      r = normalize(r); // Normalize after transform
      col += solve(Ray(p,r));
    }
  }
  col /= AA*AA;
  col *= 2.0/(1.0+col);
  col = pow(col,vec3(0.4545));
  if (alert) col.r = 1.0;
  fragColor = vec4(col,1);
}

