bool alert = false;
void assert(bool b) {
  if (!b) alert = true;
}

const float PI = 3.14159;

// Rotate vector p by angle t.
vec2 rotate(vec2 p, float t) {
  return cos(t)*p + sin(t)*vec2(-p.y,p.x);
}

bool even(int n) { return (n&1) == 0; }
bool odd(int n) { return !even(n); }

int idiv(int a,int b) {
  return a >= 0 ? a/b : -((-a+b-1)/b); // Careful with bracket here!
}

int imod(int a, int b) {
  return a-b*idiv(a,b);
}

// From Chris Wellons: https://nullprogram.com/blog/2018/07/31/
uint ihash(uint x) {
    x ^= x >> 16;
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    x ^= x >> 16;
    return x;
}

vec3 h2rgb(float h) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = smoothstep(0.0,1.0,rgb);
  return rgb;
}

