//////////////////////////////////////////////////////////////////
//
// Rotational Symmetry SDF, mla, 2022
// Exact distance to a shape with purely rotational symmetries.
// Fold down to a fundamental region, but unlike the dihedral case
// we need to take account of adjacent regions to correctly
// compute the SDF, though we only need to check the adjacent
// half regions, so drawing 2 region's worth in total.

// Here we also draw a non-convex polygon, using winding number
// to find the interior.
//
// <mouse>: set rotation and pattern parameter
// 'r': autorotation
// 's': skip the folding step
// 'x': show fundamental region boundaries
//
//////////////////////////////////////////////////////////////////

int wind = 0;
void polywind(vec2 p, vec2 q, vec2 r) {
  // NR winding number computation
  // Conveniently, this works by shooting out a ray to right, which
  // handily fits in with our fundamental region construction
  // which also makes the region face out to the right.
  q -= p; r -= p; // Relative to p
  float t = determinant(mat2(q,r));
  if (q.y <= 0.0) {
    if (r.y > 0.0 && t > 0.0) wind++;
  } else {
    if (r.y <= 0.0 && t < 0.0) wind--;
  }
}

float segment(vec2 p, vec2 q, vec2 r) {
  polywind(p,q,r);
  p -= r; q -= r;
  float k = dot(p,q)/dot(q,q);
  k = clamp(k,0.0,1.0);
  return distance(p,k*q);
}

float ray(vec2 p, vec2 r, vec2 q) {
  p -= r; q -= r;
  float k = dot(p,q)/dot(q,q);
  k = max(k,0.0);
  return distance(p,k*q);
}

float polydist(float N, vec2 p) {
    float k = PI/N;
    float s = cos(k), t = sin(k);
    float theta = atan(p.y,p.x);
    if (!key(CHAR_S)) theta = mod(theta+k,2.0*k)-k; 
    // The nearest point in the pattern could be in either of the
    // two nearest segments, so need to check them both.
    float r = 0.5*sin(0.5*iTime)+1.0;
    if (iMouse.x > 0.0) r = 2.0*iMouse.y/iResolution.y;
    p = length(p)*vec2(cos(theta),sin(theta));
    vec2 q3 = vec2(r,0);
    vec2 q0 = rotate(2.0*k)*q3;
    vec2 q6 = rotate(-2.0*k)*q3;
    vec2 q1 = vec2(0.75,0);
    vec2 q4 = rotate(-2.0*k)*q1;
    vec2 q2 = 0.4*q0;
    vec2 q5 = 0.4*q3;
    float d = 1e8;
    d = min(d,segment(p,q0,q1));
    d = min(d,segment(p,q1,q2));
    d = min(d,segment(p,q2,q3));
    d = min(d,segment(p,q3,q4));
    d = min(d,segment(p,q4,q5));
    d = min(d,segment(p,q5,q6));
    return d;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    vec2 p = 1.1*(2.0*fragCoord-iResolution.xy)/iResolution.y;
    if (!key(CHAR_R)) p *= rotate(-0.2*iTime);
    float N = 5.0;
    if (iMouse.x > 0.0) N = floor(10.0*iMouse.x/iResolution.x)+3.0;
    float d = polydist(N,p);
    float px = fwidth(p.x);
    vec3 col = vec3(0.75+0.25*cos(20.0*PI*d));
    if ((wind&1) != 0) col.b = 0.0;
    col = mix(vec3(1,0,0),col,vec3(smoothstep(0.0,px,d-0.005)));
    if (key(CHAR_X)) {
      float d = 1e8;
      d = min(d,ray(p,vec2(0),vec2(cos(PI/N),sin(PI/N))));
      d = min(d,ray(p,vec2(0),vec2(cos(PI/N),-sin(PI/N))));
      d = min(d,ray(p,vec2(0),vec2(cos(2.0*PI/N),sin(2.0*PI/N))));
      d = min(d,ray(p,vec2(0),vec2(cos(2.0*PI/N),-sin(2.0*PI/N))));
      d = min(d,ray(p,vec2(0),vec2(1,0)));
      col = mix(vec3(0),col,smoothstep(0.0,px,d-0.0025));
    }

    col = pow(col,vec3(0.4545));
    fragColor = vec4(col,1.0);
}

