#define pi 3.14159
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define thc(a,b) tanh(a*cos(b))/tanh(a)
#define pal(t,b) .5+.5*cos(2.*pi*(t+b))

float seg(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba * h);
}

// Distance function for each cell (ipos unused)
float dist(vec2 ipos, vec2 fpos, vec2 uv, float io, float t) {
    float r = 0.25 + 0.25 * cos(4. * length(uv) - t);
    vec2  p = r * vec2(cos(io + t), sin(io + 2. * t));
    float c = 0.5 + 0.5 * cos(10. * length(uv) - 2. * t);
    return seg(fpos, c * uv, c * p);
}

// Min distance for this cell and neighbour cells
float dist2(vec2 ipos, vec2 fpos, vec2 uv, float io, float t) {
    float d = 1e5;
    for (float i = -1.; i <= 1.; i++) 
        for (float j = -1.; j <= 1.; j++) 
            d = min(d, dist(ipos, fpos - vec2(i,j), uv, io, t));
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy;
    vec2 ouv = (fragCoord - 0.5 * res) / res.y;

    float t = iTime;  
    // Unused time stuff
    //float l = 2. * log(1. + length(ouv));
    //float f = pow(fract(t - l), 2.);
    //t = floor(t - l) + smoothstep(0.,1.,f);
    //t = mix(t, iTime, 1.-exp(-0.25 * iTime));
    //t = floor(t) + smoothstep(0., 1., fract(t));    
    float th = tanh(0.25 * t);

    float s = 0.;

    // Loop different layers (grids)
    float n = 40.; // 50. looks better maybe
    for (float i = n - 1.; i >= 0.; i--) {
        float io = 2. * pi * i / n;     
        float xp = 1. - exp(-0.12 * i);        

        // Rotate each layer (oscillate between 2 rotation modes)
        float c = 0.5 + 0.5 * cos(io - t);
        vec2 uv1 = ouv * rot(0.2 * t + io);
        vec2 uv2 = ouv * rot(0.4 * t + io * 10.);
        float c2 = 0.5 - 0.5 * thc(3., 0.1 * io - 0.25 * t);
        vec2 uv = (1. + 0.2 * c * i) * mix(uv1, uv2, c2);

        // Scaled grid for each layer
        float sc = 1. + 0.4 * sqrt(i);
        vec2 ipos = floor(sc * uv) + 0.5;
        vec2 fpos = sc * uv - ipos;

        // Distance function for each cell (on each layer)
        float d = dist2(ipos, fpos, uv, io, t);
        float r = 0.13 * (0.75 + 0.25 * cos(io + t));
        float k = (1. + 3. * xp) * sc / res.y;
        //float s2 = smoothstep(-k, k, -abs(-d + r) + 0.01);
        float r2 = 0.05 * cos(10. * d + uv.x * (20. + 3. * i));
        float s2 = smoothstep(-k, k, -abs(-d + r) + r2);

        // Add stuff to s (can replace float with vec3, and s with col)
        s *= 1. - s2; // s = mix(s, 0., s2);
        float fog = exp(0.02 - 0.5 * dot(uv,uv));      
        s = mix(s, fog, (0.5 - 0.4 * th) * xp);
    }

    s = clamp(s, 0., 1.); 
    vec3 col = vec3(s);

    // Contrast mode
    //col = vec3(pow(4.*s*(1.-s),2.));

    // Blood mode
    //col *= pal(0.15 * length(ouv), vec3(0,1,2)/3.);

    // Yellow mode
    col *= pal(0.15 * length(ouv), 0.5 * vec3(0,1,2)/3.);

    // Gamma correction
    col = pow(col, vec3(1./2.2));

    // Black bars at start
    col *= step(abs(ouv.x), 0.52 * res.x/res.y * th);

    fragColor = vec4(col,1.0);
}

