// Rotates a 2d point by an angle
#define Rotate2D(p,a) p = cos(a) * p + sin(a) * vec2(-p.y, p.x)
#define PI acos(-1.0)

// Dot size and torus sections
const float dotSize = 0.02;
const float torusSectionsA = 12.0, torusSectionsB = 24.0;
const float torusRadiusA = 0.3, torusRadiusB = 0.6;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalised coords (from -1 to 1)
    vec2 uv = fragCoord * 2.0 - iResolution.xy;
    uv /= iResolution.xx;

    // base colour
    vec3 col = vec3(1.0, 0.45, 0.55);

    // pixel size (for AA)
    float pixSize = 2.0 / iResolution.x;

    // We start with a point offset a bit from the centre
    vec3 p = vec3(0,torusRadiusA,0);

    // Iterate to rotate the point to form a circle
    for (float i=0.0; i<=1.0; i+=1./torusSectionsA) {

        // Make a copy of the point and rotate it on the xy plane
        vec3 pos1 = p;
        Rotate2D(pos1.xy, i * PI * 2.);

        // Shift it across so it's a cross section through one side of the torus
        pos1.x += torusRadiusB;

        // We now have a slice of torus, iterate to rotate around a second circle to finish it
        for(float j = 0.0; j <= 1.0; j+= 1. / torusSectionsB) {

            // make a copy and roate again, this time on the xz plane
            vec3 pos2 = pos1;
            Rotate2D(pos2.xz, j * PI * 2.);

            // rotate the whole donut so it spins in 3d
            Rotate2D(pos2.xy, iTime * .55);
            Rotate2D(pos2.xz, iTime * .34);

            // a bit of fudged perspective...
            pos2.z = -pos2.z * 0.5 + 0.5;
            pos2.xy *= pos2.z+0.5;

            // add to the colour if we're inside the dot
            // The dot size gets scaled by the z position for perspective
            // Smoothstep is used betwee the dotsize and dotsize + pixSize, which gives us some AA
            col += 1. - smoothstep(dotSize * pos2.z, dotSize * pos2.z + pixSize, length(uv - pos2.xy));
        }
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

