float Minv3 (vec3 p);
float Maxv3 (vec3 p);
float SmoothMax (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
vec3 HsvToRgb (vec3 c);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
float ShowInt (vec2 q, vec2 cBox, float mxChar, float val);
float Hashfv2 (vec2 p);
vec2 Hashv2v2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

#define N_CAR 5   // any reasonable value
#define PLEN 17

vec4 cPath[PLEN], carPos[N_CAR];
vec3 qHit, sunDir;
vec2 cId, cMid;
float dstFar, tCur, gSize, tEnd[PLEN + 1], tLen, trVel, cDir, cType, gHt, bHt, trSzFac;
int idObj;
const int idGrnd = 1, idFenc = 2, idRail = 3, idSup = 4, idBld = 5, idMast = 6, 
   idWhl = 7, idCar = 8;
const float pi = 3.1415927, sqrt3 = 1.7320508;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float Ddot (vec2 p)
{
  return dot (p, p);
}

vec3 TruchSDist (vec2 p)
{ // (from "Forest Train Ride")
  vec2 pc, pc1, pc2, dp;
  float d, d1, d2, cxy, rc, ac, s, ss;
  bool ct;
  ct = (cType == 2. || cType == 4.);
  if (ct) {
    pc1 = - vec2 (0., cDir);
    pc2 = vec2 (sqrt3/2. * sign (p.x), 0.5 * cDir);
    d1 = Ddot (p - pc1);
    d2 = Ddot (p - pc2);
    d = min (d1, d2);
    pc = (d == d1) ? pc1 : pc2;
    rc = 0.5;
    d = abs (sqrt (d) - rc);
  } else {
    if (cDir != 0.) p = 0.5 * vec2 (p.x - cDir * sqrt3 * p.y, cDir * sqrt3 * p.x + p.y);
    pc1 = vec2 (sqrt3/2. * sign (p.x), 0.);
    pc2 = vec2 (sqrt3 * sign (p.x), 0.);
    d1 = sqrt (Ddot (p - pc1));
    d2 = abs (sqrt (Ddot (p - pc2)) - 1.5);
    d = min (d1, d2);
    pc = (d == d1) ? pc1 : pc2;
    rc = (d == d1) ? 0.: 1.5;
  }
  dp = p - pc;
  cxy = cId.x - cId.y;
  s = (ct && cxy < 0. || ! ct && abs (cxy - 2.) == 1.) ? -1. : 1.;
  ac = (0.5 - atan (dp.y, - dp.x) / (2. * pi)) * s;
  if (! ct && abs (cxy - 2.) <= 1.) ac += 1./6.;
  ss = sign (length (dp) - rc);
  return vec3 (d * ss, rc * ss * s, ac);
}

bool OnTrk (vec2 w)
{
  vec2 wp, wm;
  float cxy;
  bool cyo, offTrk;
  cxy = cId.x - cId.y;
  cyo = (mod (cId.y, 2.) == 1.);
  wm = Rot2Cs (w, sin (- pi / 3. + vec2 (0.5 * pi, 0.))) - vec2 (0., 0.3);
  wp = Rot2Cs (w, sin (pi / 3. + vec2 (0.5 * pi, 0.)));
  offTrk = (cxy == -2. && wm.y > 0. ||
     ! cyo && (cxy == -3. && w.y > -0.3 || cxy == -2. || cxy == -1. && wm.y > 0. ||
     cxy == 1. && wm.y < 0. || (cxy == 2. || cxy == 3.) && w.x < 0. || cxy == 4. || cxy == 5.) ||
     cyo && (cxy == -3. || cxy == 0. && wp.x > 0. || (cxy == 1. || cxy == 2.) && w.x > 0. || 
     cxy == 3. || cxy == 4. && wm.x < 0. || cxy == 5. && wm.y < 0.));
  return ! offTrk;
}

void SetPath ()
{
  float ts, tl;
  ts = 1.;
  tl = 1.5;
  cPath[ 0] = vec4 (0., 0., -1./6., tl);
  cPath[ 1] = vec4 (1., 0., 1./3., - ts);
  cPath[ 2] = vec4 (0., 1., 5./6., tl);
  cPath[ 3] = vec4 (-1., 1., -1./3., ts);
  cPath[ 4] = vec4 (-1., 2., 1., ts);
  cPath[ 5] = vec4 (0., 1., - 1./6., tl);
  cPath[ 6] = vec4 (1., 1., 1./6., tl);
  cPath[ 7] = vec4 (1., 2., 1., ts);
  cPath[ 8] = vec4 (2., 1., 1./2., - tl);
  cPath[ 9] = vec4 (2., 0., -1./2., tl);
  cPath[10] = vec4 (3., -1., -1./6., tl);
  cPath[11] = vec4 (4., -1., 1./3., - ts);
  cPath[12] = vec4 (3., 0., -1./2., - tl);
  cPath[13] = vec4 (3., 1., 1./2., tl);
  cPath[14] = vec4 (2., 2., 5./6., tl);
  cPath[15] = vec4 (1., 2., -1./3., ts);
  cPath[16] = vec4 (1., 3., 1., ts);
  tEnd[0] = 0.;
  for (int k = 0; k < PLEN; k ++) tEnd[k + 1] = tEnd[k] + abs (cPath[k].w);
  tLen = tEnd[PLEN];
}

vec2 EvalPPos (float t)
{
  vec4 cp;
  vec2 tp, vd;
  float tt, r, a, dc;
  t /= 3.;
  tp = floor (t / tLen) * vec2 (2.);
  t = mod (t, tLen);
  for (int k = 0; k < PLEN; k ++) {
    if (t >= tEnd[k] && t < tEnd[k + 1]) {
      cp = cPath[k];
      tt = 2. * (t - tEnd[k]) / (tEnd[k + 1] - tEnd[k]) - 1.;
      break;
    }
  }
  tp += cp.xy;
  if (abs (cp.w) == 1.5) {
    r = 1.5;
    dc = sqrt3;
    a = pi / 6.;
    tt *= sign (cp.w);
  } else {
    r = 0.5;
    dc = 1.;
    a = - sign (cp.w) * pi / 3.;
  }
  vd = vec2 (-1., 1.) * sin (pi * cp.z + vec2 (0., 0.5 * pi));
  return (HexToPix (tp) + dc * vd - r * Rot2Cs (vd, sin (tt * a + vec2 (0.5 * pi, 0.)))) * gSize;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar / trSzFac;
  for (int k = VAR_ZERO; k < N_CAR; k ++) {
    q = (p - carPos[k].xyz) / trSzFac;
    q.xz = Rot2D (q.xz, carPos[k].w);
    d = abs (SmoothMax (PrRoundBox2Df (q.xz, vec2 (0.3, 1.1), 0.4), abs (q.y) - 0.7, 0.1)) - 0.02;
    d = max (d, - min (PrBox2Df (vec2 (q.x, q.y - 0.25), vec2 (0.4, 0.25)),
       PrBox2Df (vec2 (abs (q.z) - 0.5, q.y - 0.25), vec2 (0.4, 0.25))));
    DMINQ (idCar + k);
    q.z = abs (q.z);
    q.yz -= vec2 (1.19, 0.6);
    d = max (max (PrCaps2Df (q.zy, 0.12, 0.9), max (abs (q.x) - 0.13, -0.5 - q.y)),
       max (min (0.07 - abs (q.x), 0.4 - abs (q.y)), min (0.22 - abs (q.y - 0.7), 0.07 - q.x)));
    DMINQ (idMast);
    q.xy -= vec2 (0.02, 0.85);
    d = PrCylDf (q.yzx, 0.11, 0.12);
    DMINQ (idWhl);
  }
  return dMin * trSzFac;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0002, -0.0002);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float TrObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar / trSzFac;
  for (int k = VAR_ZERO; k < N_CAR; k ++) {
    q = (p - carPos[k].xyz) / trSzFac;
    q.xz = Rot2D (q.xz, carPos[k].w);
    d = max (PrRoundBox2Df (q.xz, vec2 (0.3, 1.1), 0.4), abs (q.y) - 0.6);
    DMIN (0);
  }
  return dMin * trSzFac;
}

float TrObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 60; j ++) {
    d = TrObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 TrObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0002, -0.0002);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = TrObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05 || d > dstFar) break;
  }
  return 0.5 + 0.5 * sh;
}

void SetGConf ()
{
  float h, cxy;
  cMid = HexToPix (cId * gSize);
  h = Hashfv2 (cId);
  if (Hashfv2 (17.11 * cId) > 0.4) {
    cDir = floor (3. * h) - 1.;
    cType = 3.;
  } else {
    cDir = 2. * floor (2. * h) - 1.;
    cType = 4.;
  }
  cxy = cId.x - cId.y;
  if (cxy == 0.) cType = 1.;
  else if (abs (cxy) == 1.) cType = mod (cId.x, 2.) + 1.;
  else if (cxy == -2. || cxy == -3. || cxy == 5.) cType = 2.;
  else if (cxy == 2. || cxy == 3. || cxy == 4.) cType = 1.;
  if (cType <= 2.) {
    if (cType == 1. && (cxy == 1. || cxy == 2. || cxy == 3.)) cDir = 0.;
    else if (cType == 1. && cxy == 4.) cDir = -1.;
    else if (cType == 2. && cxy == 5.) cDir = 1.;
    else cDir = 2. * mod (cId.x, 2.) - 1.;
  }
}

float GObjDf (vec3 p)
{
  vec3 q, cm3;
  vec2 b;
  float dMin, d, dt, dWid, eWid, rc, ac, dh, h, a;
  bool onTrk;
  dWid = 0.3;
  eWid = 0.1;
  dMin = dstFar;
  if (cType > 0.) {
    q = p;
    q.xz = (q.xz - cMid) / gSize;
    cm3 = TruchSDist (q.xz);
    dt = gSize * abs (cm3.x);
    rc = abs (cm3.y);
    ac = 18. * cm3.z;
    dh = gSize * HexEdgeDist (q.xz);
    onTrk = (cType <= 2. && rc != 0. && OnTrk (q.xz));
    if (onTrk) {
      a = (fract (3. * rc * ac + 0.5) - 0.5) / 3.;
      d = PrRoundBox2Df (vec2 (dt, q.y - 0.4), vec2 (0.002, 0.01), 0.001);
      DMIN (idRail);
      if (cm3.y < 0.) {
        b = vec2 (dt - 0.18, q.y - 0.4);
        d = max (min (min (max (abs (b.x - 0.002) - 0.008, b.y), max (abs (b.y) - 0.006,
           b.x - 0.01)), max (abs (dot (b, sign (0.25 * pi + vec2 (0.5 * pi, 0.))) + 
           0.05) - 0.005, Maxv2 (b))), abs (a) - 0.0013);
        d = min (d, max (max (abs (b.x) - 0.02, abs (a) - 0.004), q.y - 0.03));
        DMIN (idSup);
      }
      if (abs (cm3.y) == 1.5 || cm3.x > 0.) {
        d = max (PrBox2Df (vec2 (dt - dWid - 0.75, fract (rc * ac + 0.5) - 0.5),
           vec2 (0.2, 0.2)), q.y - ((cm3.y > 0.) ? 4. : 2.) * bHt - gHt);
        DMIN (idBld);
      }
      d = min (length (vec2 (dt - dWid, q.y - 0.1)) - 0.008,
         max (PrRoundBox2Df (vec2 (dt - dWid, (fract (10. * rc * ac + 0.5) - 0.5) / 10.),
         vec2 (0.007, 0.002), 0.001), q.y - 0.1));
      d = max (d, eWid - dh);
      DMIN (idFenc);
    }
    d = q.y - gHt;
    h = PrBox2Df (vec2 (dh, q.y - gHt), vec2 (eWid, 0.01));
    if (onTrk) h = min (h, min (PrBox2Df (vec2 (dt, q.y - gHt), vec2 (dWid - 0.1, 0.01)),
       PrBox2Df (vec2 (abs (abs (dt - 1.05) - 0.3), q.y - gHt), vec2 (0.05, 0.01))));
    d = SmoothMax (d, - h, 0.01);
    DMIN (idGrnd);
  }
  return dMin;
}

float GObjRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM, cIdP;
  float dHit, d, s, eps;
  if (rd.x == 0.) rd.x = 0.0001;
  if (rd.z == 0.) rd.z = 0.0001;
  eps = 0.001;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = gSize / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]),
     dot (ro.xz, edN[2])) / gSize;
  pM = HexToPix (PixToHex (ro.xz / gSize));
  hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
  s = Minv3 (hv);
  cIdP = vec2 (-999.);
  dHit = 0.;
  for (int j = VAR_ZERO; j < 320; j ++) {
    p = ro + dHit * rd;
    cId = PixToHex (p.xz / gSize);
    if (cId != cIdP) {
      cIdP = cId;
      SetGConf ();
    }
    d = GObjDf (p);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
      hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
      s = Minv3 (hv);
    }
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 GObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0002, -0.0002);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = GObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float GObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 cIdP;
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  cIdP = vec2 (-999.);
  for (int j = VAR_ZERO; j < 40; j ++) {
    p = ro + d * rd;
    cId = PixToHex (p.xz / gSize);
    if (cId != cIdP) {
      cIdP = cId;
      SetGConf ();
    }
    h = GObjDf (p);
    sh = min (sh, smoothstep (0., 0.02 * d, h));
    d += max (h, 0.01);
    if (sh < 0.05 || d > gSize) break;
  }
  return 0.5 + 0.5 * sh;
}

vec4 GroundCol (vec3 p, inout vec2 vf)
{
  vec4 col4, gCol;
  vec3 cm3;
  vec2 w;
  float dt, rc, ac;
  gCol = vec4 (0.1, 0.5, 0.1, 0.1);
  if (p.y < 0.024) {
    col4 = mix (vec4 (0.5, 0.55, 0.5, 0.), gCol, smoothstep (0., 0.2,
       abs (Rot2D (p.xz, pi / 3.).x) / (5. * gSize) - 1.));
    vf = vec2 (64., 1.);
  } else {
    col4 = gCol * (0.7 + 0.3 * Fbm2 (0.5 * p.xz));
    vf = vec2 (32., 2.);
  }
  w = (p.xz - cMid) / gSize;
  cm3 = TruchSDist (w);
  dt = gSize * abs (cm3.x);
  rc = abs (cm3.y);
  ac = 18. * cm3.z;
  if (cType <= 2. && rc != 0. && OnTrk (w)) {
    if (dt < 0.025 && step (0.3, abs (fract (16. * rc * ac + 0.5) - 0.5)) > 0.)
       col4 = vec4 (0.5, 0.5, 0.4, 0.);
  }
  return col4;
}

vec3 SkyCol (vec3 rd)
{
  rd.y = abs (rd.y);
  return mix (vec3 (0.3, 0.35, 0.7), vec3 (0.8, 0.8, 0.8),
     clamp (2. * (Fbm2 (2. * rd.xz / rd.y + 0.1 * tCur) - 0.1) * rd.y, 0., 1.));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, rs, cm3, roo;
  vec2 vf;
  float dstObj, dstObjB, dstTrObj, sh, dt, rc, ac;
  int idObjB;
  bool isLit;
  vf = vec2 (0.);
  roo = ro;
  dstObjB = GObjRay (ro, rd);
  idObjB = idObj;
  dstObj = ObjRay (ro, rd);
  if (dstObjB < dstObj) {
    dstObj = dstObjB;
    idObj = idObjB;
  }
  isLit = false;
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    if (dstObj == dstObjB) {
      cId = PixToHex (ro.xz / gSize);
      SetGConf ();
      vn = GObjNf (ro);
    } else {
      vn = ObjNf (ro);
    }
    if (idObj == idRail || idObj == idBld || idObj == idSup) {
      cm3 = TruchSDist ((ro.xz - cMid) / gSize);
      dt = gSize * abs (cm3.x);
      rc = abs (cm3.y);
      ac = 18. * cm3.z;
    }
    if (idObj == idFenc) {
      col4 = vec4 (0.7, 0.7, 0.8, 0.);
    } else if (idObj == idRail) {
      col4 = vec4 (0.7, 0.7, 0.75, 0.) * (0.5 + 0.5 * smoothstep (0., 0.001,
           abs (fract (3. * rc * ac + 0.5) - 0.5) - 0.001));
    } else if (idObj == idSup) {
      col4 = vec4 (0.6, 0.6, 0.65, 0.);
      if (abs (dt - 0.183) < 0.01 && ro.y < 0.395 && ro.y > 0.03)
         col4 *= 0.8 + 0.2 * step (0.1, abs (fract (128. * ro.y + 0.5) - 0.5));
    } else if (idObj == idBld) {
      col4 = vec4 (HsvToRgb (vec3 (Hashfv2 (19.11 * cId), 0.2, 0.9)), 0.) * (0.8 +
         0.2 * smoothstep (0., 0.002, abs (fract ((ro.y + 0.5 - gHt) / bHt) - 0.5) - 0.02));
      if (abs (fract ((ro.y - gHt - 0.01) / bHt) - 0.5) < 0.15 &&
         min (abs (fract (24. * rc * ac + 0.5) - 0.5) - 0.15,
         abs (abs (dt - 1.05) - 0.07) - 0.02) < 0.) col4 = vec4 (0.3, 0.2, 0., -1.);
      else vf = vec2 (128., 0.2);
    } else if (idObj == idGrnd) {
      col4 = GroundCol (ro, vf);
    } else if (idObj == idMast) {
      col4 = vec4 (0.7, 0.8, 0.7, 0.1);
    } else if (idObj == idWhl) {
      col4 = vec4 (0.5, 0.5, 0.6, 0.1);
    } else if (idObj >= idCar) {
      col4 = vec4 (HsvToRgb (vec3 (float (idObj - idCar) / float (N_CAR), 0.8, 1.)), 0.2);
      col4 = (qHit.y < 0.65) ? col4 : vec4 (0.7, 0.7, 0.75, 0.2) * (0.95 +
         0.05 * step (0.1, abs (fract (8. * qHit.x + 0.5) - 0.5)));
      if (max (PrRoundBox2Df (qHit.xz, vec2 (0.3, 1.1), 0.4), abs (qHit.y) - 0.7) < -0.01)
         col4 *= 0.5;
      else if (abs (qHit.y + 0.2) < 0.05) col4 *= 0.8;
      else if (length (vec2 (qHit.x, qHit.y + 0.4)) < 0.15) {
        if (ShowInt (vec2 (qHit.x - 0.13 * sign (qHit.z), qHit.y + 0.46),
           0.25 * vec2 (sign (qHit.z), 0.5), 2.,
           float (idObj - idCar + 31)) != 0.) col4 = vec4 (vec3 (0.8), -1.);
        else col4 *= 0.8;
      }
      if (abs (qHit.y) < 0.6 && abs (qHit.z) < 1.) col4 *= 0.5 + 0.5 * smoothstep (0., 0.001,
         min (abs (abs (abs (qHit.z) - 0.5) - 0.5) - 0.03, abs (abs (qHit.y) - 0.6) - 0.015));
    }
    if (col4.a >= 0.) {
      rs = ro + 0.01 * vn;
      sh = min (ObjSShadow (rs, sunDir), GObjSShadow (rs, sunDir));
      if (vf.x > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
      isLit = true;
    } else {
      col = col4.rgb * (0.2 + 0.8 * max (- dot (rd, vn), 0.));
    }
  } else if (rd.y < 0.) {
    dstObj = - ro.y / rd.y;
    vn = vec3 (0., 1., 0.);
    col4 = 0.8 * vec4 (0.1, 0.5, 0.1, 0.);
    sh = 1.;
    isLit = true;
  } else {
    col = SkyCol (rd);
  }
  if (isLit) {
    col = col4.rgb * (0.3 + 0.2 * max (- dot (sunDir, vn), 0.) +
       0.7 * sh * max (dot (vn, sunDir), 0.)) +
       col4.a * step (0.95, sh) * pow (max (dot (reflect (sunDir, vn), rd), 0.), 32.);
    col = mix (col, SkyCol (rd), 1. - exp (min (0., 1. - 5. * dstObj / dstFar)));
  }
  dstTrObj = TrObjRay (roo, rd);
  if (dstTrObj < min (dstObj, dstFar)) {
    ro = roo + dstTrObj * rd;
    vn = TrObjNf (ro);
    col = mix (0.8 * col, SkyCol (reflect (rd, vn)), 0.3 +
       0.6 * pow (1. - abs (dot (vn, rd)), 5.));
  }
  return clamp (col, 0., 1.);
}

#define N_WIN  3

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col, vd, pAv;
  vec2 canvas, uv, uvv, mMid[N_WIN], ut[N_WIN], mSize, msw, pc[3];
  float el, az, zmFac, asp, sr, t, cGap, nc;
  int vuId, regId;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  mSize = (1./5.) * vec2 (asp, 1.);
  mMid[0] = (1. - mSize.y) * vec2 (- asp, 1.);
  mMid[1] = (1. - mSize.y) * vec2 (asp, 1.);
  mMid[2] = (1. - mSize.y) * vec2 (asp, -1.);
  for (int k = 0; k < N_WIN; k ++) ut[k] = abs (uv - mMid[k]) - mSize;
  regId = -1;
  if (mPtr.z > 0.) {
    regId = 0;
    for (int k = 0; k < N_WIN; k ++) {
      msw = 2. * mPtr.xy - mMid[k] / vec2 (asp, 1.);
      if (Maxv2 (abs (msw)) < mSize.y) {
        regId = k + 1;
        msw /= 2. * mSize.y;
        break;
      }
    }
    if (regId == 0) msw = mPtr.xy;
  }
  vuId = 0;
  for (int k = 0; k < N_WIN; k ++) {
    if (Maxv2 (ut[k]) < 0.) {
      uv = (uv - mMid[k]) / mSize.y;
      vuId = k + 1;
      break;
    }
  }
  if (regId > 0 && (vuId == 0 || vuId == regId)) vuId = regId - vuId;
  gSize = 8.;
  gHt = 0.03;
  bHt = 0.2;
  SetPath ();
  trSzFac = 0.1;
  trVel = 0.2;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0. && vuId == regId) {
    az -= 2. * pi * msw.x;
    el += 0.5 * pi * msw.y;
  }
  cGap = 0.3;
  pAv = vec3 (0.);
  for (int k = VAR_ZERO; k < N_CAR; k ++) {
    t = (tCur + 30.) * trVel - float (k) * cGap;
    for (int j = VAR_ZERO; j < 3; j ++)
       pc[j] = EvalPPos (t + ((j > 0) ? sign (float (j) - 1.5) * 0.06 : 0.));
    carPos[k].xz = pc[0];
    carPos[k].y = 0.22;
    pAv += carPos[k].xyz;
    vd.xz = pc[2] - pc[1];
    carPos[k].w = 0.5 * pi - atan (vd.z, vd.x);
  }
  nc = float (N_CAR);
  pAv /= nc;
  t = (tCur + 30.) * trVel;
  if (vuId == 0 || vuId == 3) {
    if (uv.x / asp > -1.+ 2. * SmoothBump (0.25, 0.75, 0.01, fract (0.025 * tCur)))
       vuId = 3 - vuId;
    ro.xz = EvalPPos (t - ((vuId == 0) ? nc + 0.5 : -1.5) * cGap);
    ro.x += 0.1;
    ro.y = (vuId == 0) ? 0.2 : 0.5;
    vd = normalize (((vuId == 0) ? carPos[N_CAR - 1].xyz : carPos[0].xyz) - ro);
    az += atan (vd.z, - vd.x) - 0.5 * pi;
    el += asin (vd.y);
    el = clamp (el, -0.15 * pi, 0.15 * pi);
    zmFac = 3.5;
    dstFar = 12. * gSize;
  } else if (vuId == 1) {
    ro = vec3 (0., 30., (-3. * sqrt3 + (2. / tLen) * t) * gSize);
    ro.xz = Rot2D (ro.xz, - pi / 3.);
    ro.xz += 0.01;
    ro.x += 1.6 * gSize;
    az += pi / 3.;
    el -= 0.2 * pi;
    el = clamp (el, -0.4 * pi, -0.13 * pi);
    zmFac = 3.;
    dstFar = 40. * gSize;
  } else if (vuId == 2) {
    ro = vec3 (0., 20., (-1.5 * sqrt3 + (2. / tLen) * t) * gSize);
    ro.xz = Rot2D (ro.xz, - pi / 3.);
    ro.xz += 0.01;
    ro.x -= 1.5 * gSize;
    vd = normalize (pAv - ro);
    az = atan (vd.z, - vd.x) - 0.5 * pi;
    el = asin (vd.y);
    zmFac = 30.;
    dstFar = 30. * gSize;
  }
  vuMat = StdVuMat (el, az);
  sunDir = normalize (vec3 (0., 2., -1.));
  sunDir.xz = Rot2D (sunDir.xz, - pi / 3.);
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a + 0.5) *
       pi)) / zmFac;
    rd = vuMat * normalize (vec3 (-2. * tan (0.5 * atan (uvv.x / asp)) * asp, uvv.y, 1.));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  for (int k = 0; k < N_WIN; k ++) {
    if (Maxv2 (ut[k]) < 0. && Minv2 (abs (ut[k])) * canvas.y < 3.) col = vec3 (0.7, 0.3, 0.3);
  }
  fragColor = vec4 (col, 1.);
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCaps2Df (vec2 p, float r, float h)
{
  return length (p - vec2 (0., clamp (p.y, - h, h))) - r;
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float HexEdgeDist (vec2 p)
{
  p = abs (p);
  return (sqrt3/2.) - p.x + 0.5 * min (p.x - sqrt3 * p.y, 0.);
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1.,
     0., 1.), c.y);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float DigSeg (vec2 q)
{
  q = 1. - smoothstep (vec2 (0.), vec2 (0.04, 0.07), abs (q) - vec2 (0.13, 0.5));
  return q.x * q.y;
}

#define DSG(q) k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q)

float ShowDig (vec2 q, int iv)
{
  vec2 vp, vm, vo;
  float d;
  int k, kk;
  vp = vec2 (0.5, 0.5);
  vm = vec2 (-0.5, 0.5);
  vo = vp - vm;
  if (iv == -1) k = 8;
  else if (iv < 2) k = (iv == 0) ? 119 : 36;
  else if (iv < 4) k = (iv == 2) ? 93 : 109;
  else if (iv < 6) k = (iv == 4) ? 46 : 107;
  else if (iv < 8) k = (iv == 6) ? 122 : 37;
  else             k = (iv == 8) ? 127 : 47;
  q = (q - 0.5) * vec2 (1.8, 2.3);
  d = 0.;
  kk = k;
  DSG (q.yx - vo);  DSG (q.xy - vp);  DSG (q.xy - vm);  DSG (q.yx);
  DSG (q.xy + vm);  DSG (q.xy + vp);  DSG (q.yx + vo);
  return d;
}

float ShowInt (vec2 q, vec2 cBox, float mxChar, float val)
{
  float nDig, idChar, s, sgn, v;
  q = vec2 (- q.x, q.y) / cBox;
  s = 0.;
  if (Minv2 (q) >= 0. && Maxv2 (q) < 1.) {
    q.x *= mxChar;
    sgn = sign (val);
    val = abs (val);
    nDig = (val > 0.) ? floor (max (log2 (val) / log2 (10.), 0.) + 0.001) + 1. : 1.;
    idChar = mxChar - 1. - floor (q.x);
    q.x = fract (q.x);
    v = val / pow (10., mxChar - idChar - 1.);
    if (idChar == mxChar - nDig - 1. && sgn < 0.) s = ShowDig (q, -1);
    if (idChar >= mxChar - nDig) s = ShowDig (q, int (mod (floor (v), 10.)));
  }
  return s;
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  return fract (sin (dot (p, cHashVA3) + vec4 (0., cHashVA3.xy, cHashVA3.x + cHashVA3.y)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

