#define pi 3.141592
#define tau pi * 2.0
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

float sdPrism(vec3 p,float f,vec2 s){
    float a = atan(p.z,p.x);
    float b = tau / f;
    float h = cos(floor(0.5 + a/b) * b-a);
	vec2 d = abs(vec2(length(p.xz)*h,p.y)) - s;
	return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float scene(vec3 p){
    p.zx *= rot(iTime*2.0);
    return sdPrism(p.yzx,3.0+floor(iTime*2.0/pi),vec2(0.5,0.25));
}

vec3 calcNormal(vec3 p){
	vec2 e = vec2(-1.0,1.0) * 0.0001;
	return normalize(vec3(
	  e.yyx * scene(p + e.yyx) +
	  e.xyy * scene(p + e.xyy) +
	  e.yxy * scene(p + e.yxy) +
	  e.xxx * scene(p + e.xxx)
	));
}

float calcAO(vec3 p,vec3 n){
	float occ = 0.0;
	float sca = 1.0;

	for(int i=0;i<5;i++){
        float hr = 0.01+0.15*float(i)/4.0;
        vec3 aopos = n*hr + p;
        float dd = scene(aopos);
        occ += -(dd-hr)*sca;
        sca *= 0.95;
	}

	return clamp(1.0-occ*1.5,0.0,1.0);
}

float raymarch(vec3 ro,vec3 rd){
    float d = 0.0;
    for(int i=0;i<256;i++){
        vec3 p = ro+rd*d;
        float h = abs(scene(p));
        d += h;
        if(h < 0.001 || d > 20.0) break;
    }
    if(d > 20.0) d = -0.0;
    return d;
}

void mainImage(out vec4 fragColor,vec2 fragCoord){
  vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
  vec3 col = vec3(0.0);

  vec3 ro = vec3(0.0,0.0,3.0);
  vec3 rd = normalize(vec3(uv,-2.0));

  float d = raymarch(ro,rd);
  vec3 pos = ro+rd*d;

   if(d > 0.0 && d < 20.0){
  	vec3 nor = calcNormal(pos);

    vec3 lig = normalize(vec3(0.5));
  	float dif = clamp(dot(nor,lig),0.0,1.0);
  	float occ = calcAO(pos,nor);
    vec3 hal = normalize(lig-rd);
  	float spe = pow(clamp(dot(nor,hal),0.0,1.0),16.0)*dif*(0.04+0.96*pow(clamp(1.0-dot(hal,-rd),0.0,1.0),5.0));
  	float amb = clamp(0.5+0.5*nor.y,0.0,1.0);
  	float sha = raymarch(pos+nor*0.02,lig);
  	if(sha > 20.0) sha = -1.0;
  	dif *= step(sha,0.0);
  	spe *= dif;
  	col += vec3(0.5,1.0,1.2)*amb*occ;
  	col += vec3(2.8,2.2,1.8) * dif;
  	col *= 0.22;
  	col += vec3(2.8,2.2,1.8) * spe*3.0;
  }

  col = pow(col,vec3(1.0/2.2));

  fragColor = vec4(col,1.0);
}

#define SC (250.0)

#define OCTAVES 8
float fbm(in vec2 uv,int octaves)
{
    //this function generates the terrain height
    uv *= 1.25;
    float value = 0.;
    float amplitude = 1.;
    float freq = 0.8,n2=0.;
    vec2 n1 = vec2(0.);
    for (int i = 0; i < octaves; i++)
    {
        value = abs(value-abs(n2-value) * amplitude);
        n2 = max(sin(uv.x*freq)+cos(uv.y*freq),-n2);

        // From Dave_Hoskins https://www.shadertoy.com/user/Dave_Hoskins
        value = min(value,abs(value-abs(n2-value) * amplitude));

        amplitude *= .37;

        freq *= 2.05;

        uv = vec2(uv.y,uv.x+n2/(freq));
    }

    return value;
}

float fbm(in vec2 uv){
    return fbm(uv,OCTAVES);
}

float f(in vec3 p,int iters)
{   
    float h = fbm(p.xz,iters);
    return h;
}

float f(in vec3 p)
{   
    float h = fbm(p.xz,12);
    return h;
}

vec3 getNormal(vec3 p, float t)
{
    vec3 eps=vec3(.001 * t, .0, .0);
    vec3 n=vec3(f(p - eps.xyy) - f(p + eps.xyy),
                2. * eps.x,
                f(p - eps.yyx) - f(p + eps.yyx));

    return normalize(n);
}

float rayMarching(in vec3 ro, in vec3 rd, float tMin, float tMax)
{
    float t = tMin;
	for( int i = 0; i < 300; i++ )
	{
        vec3 pos = ro + t * rd;
		float h = pos.y - f(pos,OCTAVES);
		if( abs(h) < (0.0015 * t) || t > tMax ) 
            break;
		t += 0.4 * h;
	}

	return t;
}

vec3 lighting(vec3 p, vec3 normal, vec3 L, vec3 V)
{
    vec3 sunColor = vec3(1., .956, .839);
