    vec3 col = vec3(0.2, 0.1, 0.4) * (1.0-acos(rdWS.y)/PI);

    if(dist < MAXDIST)
    {
        col = norm(roWS + rdWS*dist)*0.5+0.5 + step(55., dist);
    }

    return vec4(col, 1.0);
