#define PI 3.14159265
#define IPI .318309886
#define D2R .0174532925
#define R2D 57.2957795

#define MAXDIST 60.
#define MAXSTEPS 30 //Bandaid - deal with corners that make steps tiny - will only be used for few rays

#define SDEPS 0.007
#define NORMALEPS 0.001

//HELP

vec3 arot(vec3 vec, vec3 axis, float theta) //axis rotation
{
    vec3 proj = vec - dot(axis, vec) * axis;
    vec3 up = normalize(cross(proj, axis));

    return dot(vec, axis) * vec + normalize(proj) * cos(theta) * length(proj) + up * sin(theta) * length(proj);
}

vec3 crot(vec3 vec, vec3 pole, float minTheta) //clamp rotation
{
    float correctTheta = max(0., minTheta - acos(dot(normalize(vec), pole)));
    vec3 axis = normalize(cross(vec, pole));

    return arot(vec, axis, correctTheta);
}

vec3 crotdoubleend(vec3 vec, vec3 pole, float minTheta) //clamp rotation on both ends of the pole
{
    return crot(crot(vec, pole, minTheta), -pole, minTheta);
}

vec3 proj(vec3 vec, vec3 axis)
{
    return vec - dot(vec, axis) * axis;
}

//SDFs

float sdRectPrism(vec3 p, vec3 forward, vec3 up, float thickness, float len) //origin = vec3(0.0)
{
    vec3 right = cross(forward, up);

    p = vec3(dot(p, right), dot(p, up), dot(p, forward));
    p = vec3(p.x - .5*thickness, p.y - .5*thickness, p.z - len * .5);
    p = abs(p);

    float dist2D = 0.0;

    float distX = p.x - .5*thickness;
    float distY = p.y - .5*thickness;
    float distZ = p.z - .5 * len;

    if(distX > 0. && distY > 0.)
    {
        dist2D = sqrt(distX*distX + distY*distY);
    }
    else if(distY > 0.)
    {
        dist2D = distY;
    }
    else if(distX > 0.)
    {
        dist2D = distX;
    }
    else
    {
        return max(distZ, max(distX, distY));
    }

    return sqrt(pow(dist2D,2.) + pow(max(0., distZ),2.));

}
float sdEdgedCube(vec3 p, mat4 transform, float thickness) //column order -> p(rows) * transform(columns)
{
    //cube is in (-1, 1) after transformation
    p = abs(vec4(p, 1.0) * transform).xyz;

    //3 distances to the edges
    p = p - vec3(1., 1., 1.);

    float distX = sdRectPrism(p, vec3(-1.0, 0.0, 0.0), vec3(0.0, 0.0, -1.0), thickness, 1.);
    float distY = sdRectPrism(p, vec3(0.0, -1.0, 0.0), vec3(-1.0, 0.0, 0.0), thickness, 1.);
    float distZ = sdRectPrism(p, vec3(0.0, 0.0, -1.0), vec3(0.0, -1.0, 0.0), thickness, 1.);

    return min(distX, min(distY, distZ));
}

