ivec2 dataOffset = ivec2(0);
float startWait = .1;

vec3 SS2WS(vec2 p, vec3 forward, float fovY)
{
    //to view space
    float y = tan(fovY*.5);
    float x = y * iResolution.x/iResolution.y;

    //to world space
    vec3 up = normalize(vec3(0.0, 1.0, 0.0) - dot(forward, vec3(0.0, 1.0, 0.0)) * forward);
    vec3 right = cross(up, forward);

    return normalize(right*p.x*x + up*p.y*y + forward);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 mouseCurr = iMouse;
    vec4 mousePrev = iTime > startWait ? texelFetch(iChannel0, dataOffset+ivec2(1,0), 0) : mouseCurr;

    //Get camera data
    vec3 camPos = iTime > startWait ? texelFetch(iChannel0, dataOffset+ivec2(0,1), 0).xyz : vec3(0.0, 0.0, -24.0);
    vec3 camForward = iTime > startWait ? texelFetch(iChannel0, dataOffset+ivec2(1,1), 0).xyz : vec3(0.0, 0.0, 1.0);

    //get previous slide data
    //vec4 slideOld = iTime > startWait ? texelFetch(iChannel0, dataOffset+ivec2(0,2), 0) : vec4(0.0);

    vec3 slide = SS2WS((mouseCurr.xy/iResolution.xy)*2.0-1.0, camForward, 60.*D2R) - SS2WS((mousePrev.xy/iResolution.xy)*2.0-1.0, camForward, 60.*D2R);
    vec3 slideAxis = (sign(mousePrev.z) == 1.0 && sign(mouseCurr.z) == 1.0) ? normalize(cross(slide, camPos)) : vec3(0.);

    camPos = (length(slideAxis) >= 1.) ? crotdoubleend(arot(camPos, slideAxis, -length(slide)*10.0), vec3(0.0, 1.0, 0.0), PI*.2) : camPos;
    camForward = normalize(-camPos);

    //Store data
    if(ivec2(fragCoord) == dataOffset + ivec2(0,0)) {fragColor = mousePrev;}
    if(ivec2(fragCoord) == dataOffset + ivec2(1,0)) {fragColor = mouseCurr;}

    if(ivec2(fragCoord) == dataOffset + ivec2(0,1)) {fragColor = vec4(camPos, 1.0);}
    if(ivec2(fragCoord) == dataOffset + ivec2(1,1)) {fragColor = vec4(camForward, 1.0);}

    //if(ivec2(fragCoord) == dataOffset + ivec2(0,2)) {fragColor = vec4(slideAxis, slideMag);}
}

