void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    // Random points
    vec2 p0 = vec2(sin(iTime*.72)*.5+.5, sin(iTime*1.21-.34)*.5+.5);
    vec2 p1 = vec2(sin(iTime*.31+2.53)*.5+.5, sin(iTime*.13+.92)*.5+.5);
    vec2 p2 = vec2(sin(iTime*.96-5.35)*.5+.5, sin(iTime*.35-1.41)*.5+.5);
    vec2 p3 = vec2(sin(iTime*.15+.56)*.5+.5, sin(iTime*.26+.51)*.5+.5);

    // Length of each point to current sample (uv)
    float p0Len = length(p0 - uv);
    float p1Len = length(p1 - uv);
    float p2Len = length(p2 - uv);
    float p3Len = length(p3 - uv);

    vec3 col = vec3(0);

    // Minimum Length
    float mLen = min(p0Len, min(p1Len, min(p2Len, p3Len)));

    // Each point gets a unique color
    if(mLen == p0Len)
        col = vec3(1,0,0);
    else if(mLen == p1Len)
        col = vec3(1,1,0);
    else if(mLen == p2Len)
        col = vec3(0,1,0);
    else if(mLen == p3Len)
        col = vec3(0,1,1);

    // Show the point
    col = col * step(.05, mLen);    

    fragColor = vec4(col, 1.);
}

// Turns color to water color
vec3 getWaterColor(vec3 color) {
    // Makes so each channel of the color has definition of "bands" (instead of normal color definition of 256)  
    const float bands = 6.0;
    vec3 col = round(color * bands) / bands;
    // "length(col)" make brightness of "color" limited, since "col" has limited definition and we are grabbing brightness from that
    // While "normalize(color)" get's only color, which has normal definition
    // So multiplying length(col) with normalize(color) gives color which has low definition brightness but high definition color
    // Making it look similar to watercolor painting
    // Then it interpolates normal color with this constrained color to add a bit of detail
    return mix(color, length(col) * normalize(color), 0.7);
}

// get's random number for 2D coordinate
float rand(vec2 uv) {
	return fract(sin(dot(vec2(12.9898,78.233), uv)) * 43758.5453123);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    // Split's screen in 2 parts for comparision
    if(uv.x >= 0.5) // watercolored texture color
    {
        // this adds some randomness to the uv, making it look grainy        
        vec2 ruv = clamp(uv + (vec2(rand(uv), rand(uv + vec2(13.61, -21.35))) * 2.0 - 1.0) / iResolution.xy * 2., vec2(0), vec2(1));
        // mixing between grainy and smooth texture
        vec3 t = mix(textureLod(iChannel0, ruv, 2.0).rgb, textureLod(iChannel0, uv, 2.0).rgb, 0.5);
        fragColor.rgb = getWaterColor(t);
    }
    else // normal texture color
    {
        fragColor.rgb = texture(iChannel0, uv).rgb;
    }

    // middle line in split screen
    float d = abs(uv.x - 0.5) / 0.005;
    if(d < 1.0) 
    {
        // Added smoothstep interpolation to make middle line look smoother
        fragColor.rgb = mix(vec3(0), fragColor.rgb, smoothstep(0.0, 1.0, d * d));
    }
}

// From https://www.shadertoy.com/view/ltfSRr
float message(vec2 uv) {
	uv -= vec2(1.0, 10.0);
	if ((uv.x < 0.0) || (uv.x >= 32.0) || (uv.y < 0.0) || (uv.y >= 3.0)) return -1.0;
	int i = 1, bit = int(exp2(floor(32.0 - uv.x)));
	if (int(uv.y) == 2) i = 928473456 / bit;
	if (int(uv.y) == 1) i = 626348112 / bit;
	if (int(uv.y) == 0) i = 1735745872 / bit;
	return float(i - 2 * (i / 2));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	if (iResolution.y < 2000.0) {
		float c = message(fragCoord / 8.0);
		if (c >= 0.0){
			fragColor = vec4(c);
			return;
		}
	}
	fragColor = vec4(fragCoord / iResolution.xy, 0.5 + mod(iTime, 0.5), 1.0);
}

