    vec3 g = vec3(0.0, 1.0, 0.0);
    vec3 b = vec3(0.0, 0.0, 1.0);
    float thickness = 0.004;     

    // Lines concept by MatHack
    // --- https://www.shadertoy.com/view/4sVSzw
    for(int k=0; k<WAVES; k++) {
        float i = float(k);

        float K = 2.5 + 3.;
        float B = 1.0 + 2.0;
        float x = uv.x * 3.;
