        float att = (1.0 - (i*0.3)) * 0.4 * bounce;
        float split = uv.y + (att*pow((K/(K+pow(x, K))), K) * cos((B*x)-(time+(i*10.*phi))));

        vec3 c;

        if (0 == k) {
            c = r;
        }
        else if (1 == k) {
            c = g;
        }
        else if (2 == k) {
            c = b;
        }

        vec3 line = smoothstep(0.0, 1.0-phi0, abs(thickness / split)) * c;
        cc += vec4(line, 1.0);
    }
    // ---

    fragColor = cc;
}

