vec2
process_beat (float base, float beat, float time) {
    float l = sin(TWOPI * (base + 00.0) * time);
    float r = sin(TWOPI * (base + beat) * time);

    return vec2 (l, r);
}

vec2
mainSound (int samp, float time) {
    float volume = phi0;
    float base = 432.0;

    vec2 s1 = process_beat (base / (phi),    beat, time);
    vec2 s2 = process_beat (base / (PI*phi), beat, time);
    vec2 s = vec2 (0.0);
         s = mix (s, s1, 1.00);
         s = mix (s, s2, phi0i);

    return volume * s;
}

