float smooth_damier(vec2 uv){
    float a = (.32035*atan(sin(PI*uv.x)/0.05)+.5);
    float b = (.32035*atan(sin(PI*uv.y)/0.05)+.5);
    return a*(1.-b)+b*(1.-a);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uvf = (fragCoord.xy/iResolution.xy);
    ivec2 uv = ivec2(fragCoord.xy);


    float D = texture(iChannel0, uvf).z;
    vec4 sl = A(uvf-dx);
    vec4 sr = A(uvf+dx);
    vec4 su = A(uvf-dy);
    vec4 sd = A(uvf+dy);

    vec3 normal = normalize(vec3(uvf, sl.z-sr.z) + vec3(uvf, su.z-sd.z) + vec3(uvf, D));


    vec3 col = vec3(.0, .7, .8)*.7;

    vec3 ld = vec3(1.,1.,2.);

    vec3 rd = vec3(uvf, -1.);

    float L = dot(normal, ld);
    L = pow(L, .5);

    // Some refraction effect
    vec3 rr = refract(rd, normal, .1);
    vec3 ro = vec3(uv, D);
    float depth = -ro.z/rr.z*20.;
    vec2 st = (ro+rr*depth).xy;

    // Smoothed version of a chessboard
    float d = clamp(smooth_damier(st/40.)+.6, 0., 1.);

    // Ambient + Diffuse lightning
    col = col*L*4. + 5.*col*(.5+.5*normal.z);

    // Gamma correction
    col = vec3(3.*pow(d*col/8., vec3(3.)));

    fragColor = vec4(col, 1.);
    }

