const float v = .2; // Viscosity factor
const float K = .1; // Normalization factor
const float dt = .1; // delta t
const float kappa = .6; // Diffusion factor
const float N = 2.; // Advection factor


float Bound(vec2 uvf){
    float res;
    res = 1.-smoothstep(.49, .5, abs(uvf.y-0.5)); 
    res *= 1.- smoothstep(.49, .5, abs(uvf.x-0.5));
    return res;
}

