#define A(u) textureLod(iChannel0, u, 0.)
#define R iResolution.xy
#define dx vec2(1./iResolution.x, 0)
#define dy vec2(0, 1./iResolution.y)
vec4 init_lattice(vec2 uv){
    return vec4(0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 m = (vec2(cos(iTime*3.), sin(iTime*6.))*.3+.5);
    if (iMouse.z > 0.){
        m = iMouse.xy/iResolution.xy;
    }
    ivec2 uv = ivec2(fragCoord.xy);
    vec2 uvf = fragCoord.xy/R.xy;

    vec4 state;    
    // Init lattice
    if (iFrame < 1){
        state = init_lattice(uvf);
    }    

    else{
        // X,Y is for velocity, Z for density, W is not used here.
        state = A(uvf);                

        float S = K/dt;
        vec4 sl = A(uvf-dx);
        vec4 sr = A(uvf+dx);
        vec4 su = A(uvf-dy);
        vec4 sd = A(uvf+dy);


        vec4 Udx = (sr-sl)*.5; // Differential respect to x
        vec4 Udy = (su-sd)*.5; // Differential respect to y

        float Udiv = Udx.x + Udy.y; // Velocity divergence
        vec2 DdX = vec2(Udx.z, Udy.z); // density's gradient

        // Diffusion        
        state.z -= kappa*dt* dot(vec3(DdX, Udiv), state.xyz);        

        // Pressure Thing's
        vec2 PdX = S*DdX; // grad P = K * grad rho
        vec2 L = (sl.xy + sr.xy + su.xy + sd.xy) - 4.*state.xy; // velocity's laplacian 
        vec2 viscosity = v*L; 

        // Semi Lagrangian advection
        vec2 where = uvf - (dt*state.xy)*N/iResolution.xy; 
        state.xy = A(where).xy;

        // Correct the advection to reduce the divergence. 
        // It's not a real constraint on divergence since the divergences is not 0
        // after this correction. 
        // But it correct it enough to allow a full explicit scheme of the equation
        state.xy += dt*(viscosity - PdX);

        // Change density when click
        state.z += 3.*(1.-smoothstep(0., .02, length(uvf-m)));

        // Boundaries
        state.xy *= Bound(uvf);

        state.z *= .999; // Stability

        // Clamping in order to mitigate exploding scheme. This setting is completly arbitrary
        state = clamp(state, vec4(vec2(-10.), .5, -10.), vec4(vec2(10), 3., 10.));
    }


    fragColor = state;
}

