#define AA 1

struct Inter{
  float t;
  vec3 nor;
  float id;
};

Inter iUn(Inter a,Inter b){
  if(b.t > -1.0 && b.t < a.t) return b;
  return a;
}

Inter iSphere(float id,vec3 ro,vec3 rd,Inter a,vec4 sph){
	Inter t;

	vec3 oc = ro - sph.xyz;
	float b = dot(oc,rd);
	float c = dot(oc,oc) - sph.w*sph.w;
	float h = b*b - c;
	t.t = -b - sqrt(h);
	if(h < 0.0) t.t = -1.0;

	t.id = id;

	vec3 pos = ro+rd*iUn(a,t).t;
	t.nor = (pos-sph.xyz)/sph.w;

	return t;
}

Inter iPlane(float id,vec3 ro,vec3 rd,vec4 pln){
	Inter t;
	t.t = -(dot(rd,pln.xyz)+pln.w)/dot(rd,pln.xyz)+pln.w;
	t.id = id;
	t.nor = pln.xyz;
	return t;
}

Inter raycast(vec3 ro,vec3 rd){
	Inter t;
	t.id = -1.0;
	t.t = 99999999.9;
    t.nor = vec3(0.0);
	vec3 pos = ro+rd*t.t;

  // scene //

	t = iUn(t,iPlane(3.0,ro,rd,vec4(0.0,1.0,0.0,1.0)));
	t = iUn(t,iSphere(1.0,ro,rd,t,vec4(0.0,0.0,0.0,1.0)));
	t = iUn(t,iSphere(2.0,ro,rd,t,vec4(1.0,-0.7,1.0,0.3)));
	t = iUn(t,iSphere(0.0,ro,rd,t,vec4(-0.8,-0.85,1.0,0.15)));

	return t;
}

vec3 render(vec2 uv){
	vec3 col = vec3(0.0);

	vec3 ro = vec3(0.0,0.0,3.5);
	vec3 rd = normalize(vec3(uv,-2.0));

	Inter t = raycast(ro,rd);
	vec3 nor = t.nor;
	vec3 pos = ro+rd*t.t;
  vec3 mate = vec3(0.0);

	if(t.id == 0.0){
		mate = vec3(0.0,0.0,1.0);
	}else if(t.id == 1.0){
		mate = vec3(1.0,0.0,0.0);
	}else if(t.id == 2.0){
		mate = vec3(0.0,1.0,0.0);
	}else if(t.id == 3.0){
		mate += 0.5+0.5*mod(floor(pos.x*5.0)+floor(pos.z*5.0),2.0);
	}

	vec3 bgCol = vec3(0.5,0.5,0.5);
	vec3 fogCol = vec3(0.0,0.0,0.0);
	vec3 fog = mix(bgCol,fogCol,1.0-rd.y);

	if(t.id != -1.0){
		col = vec3(0.0);
		vec3 lig = normalize(vec3(0.5));
		float dif = clamp(dot(nor,lig),0.0,1.0);
		float sha = step(1.0,raycast(pos+0.58,lig).t);
		col += mate * dif * sha;
	}

	col = mix(col,fog,clamp(0.0001*t.t*t.t*t.t,0.0,1.0));

	//col = pow(col,vec3(1.0/3.3));

	return col;
}

void mainImage(out vec4 fragColor,vec2 fragCoord){
  vec3 col = vec3(0.0);

  #if AA > 1
  for(int i=0;i<AA;i++)
  for(int j=0;j<AA;j++){
    vec2 o = vec2(float(i),float(j));
    vec2 uv = (2.0*(fragCoord-o)-iResolution.xy)/iResolution.y;
  #else
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
  #endif
    col += render(uv);
  #if AA > 1
  }
  col /= float(AA*AA);
  #endif

  fragColor = vec4(col,1.0);
}

