#define A(u) texture(iChannel0,(u)/iResolution.xy)
#define B(u) texture(iChannel1,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    float tz = 0.;
    vec4  a  = vec4(0);
    float z  = 6.;//kernel convolution size
    for(float i=-z; i<=z; ++i){
    for(float j=-z; j<=z; ++j){
        if(length(vec2(i,j))>z-.1){continue;}
        vec4 t = A(u+vec2(i,j));
        vec4 m = B(u+vec2(i,j));
        vec2 c = (m.xy-vec2(i,j));
        float z = t.z*exp(-dot(c,c));
        a.xy += z*m.xy;
        a.z  += z*m.z;
        tz   += z;
    }}
    if(tz==0.){tz = 1.;}
    a.xy /= tz;
    //a = A(u);
    if(iMouse.z>0.)
    {
        vec2 m = 22.*(u-iMouse.xy)/iResolution.y;
        a += vec4(-normalize(m),0,0)*exp(-dot(m,m));
    }
    float keyW = texture( iChannel2, vec2(87.5/256.,.25) ).x;
    if(iFrame==0||keyW!=0.)
    {
        vec2 m = 8.*(u-iResolution.xy*.5)/450.;
        a = vec4(0,0,.5,0)-vec4(8,8,0,0)*exp(-dot(m,m));
    }
    fragColor = a;
}

