// An algorithm for drawing limit sets of Kleinian groups specified by "Grandma's Recipe" from the book 
// Indra's Pearls by David Mumford, Caroline Series, and David Wright. It's somewhat similar in spirit to
// Jos Leys' algorithm https://www.shadertoy.com/view/MtKXRh , but it also supports groups where traces of 
// both generators are complex (i.e. groups that are not Maskit groups). It's also slower and less robust,
// at least at the moment.

// The ultimate goal is to use this to render 3D limit sets (which is also why this code features lots of
// quaternions for seemingly no reason) since it should be applicable pretty directly, but I thought this
// version looked pretty OK in its own right and might be useful to someone.

// I hope it's relatively readable as well.

// Uncomment to view the estimated Schottky curves used to determine which transformation to apply at
// each iteration.
//#define DEBUG_VIEW

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 2.0;
    vec2 z = zoom*(2.0*(fragCoord.xy) - iResolution.xy) / iResolution.y;

    vec2[] ta = vec2[]
    (
        vec2(2,0),
        vec2(2,0.2),
        vec2(2,0.2),
        vec2(2,-0.2),
        vec2(2,-0.2),
        vec2(1.8,-0.2),
        vec2(1.8,-0.2),
        vec2(2,0),
        vec2(2,0),
        vec2(1.8,-0.2),
        vec2(2.2,0),
        vec2(2.1,-0.4),
        vec2(2,-0.9),
        vec2(1.9,-1.4)
    );
    vec2[] tb = vec2[]
    (
        vec2(2,0),
        vec2(2,0),
        vec2(2,0.2),
        vec2(2,0.2),
        vec2(2,-0.2),
        vec2(2,-0.2),
        vec2(1.8,-0.2),
        vec2(2,0),
        vec2(1.8,0.2),
        vec2(1.8,0.2),
        vec2(2.2,0),
        vec2(2.1,-0.4),
        vec2(2,-0.9),
        vec2(1.9,-1.4)
    );


    float time = mod(iTime/2.0,float(ta.length()));
    int index = int(floor(time));
    float blend = fract(time);
    vec2 tracea = mix(ta[index],ta[(index + 1)%ta.length()],smoothstep(0.0,1.0,smoothstep(0.0,1.0,blend)));
    vec2 traceb = mix(tb[index],tb[(index + 1)%ta.length()],smoothstep(0.0,1.0,smoothstep(0.0,1.0,blend)));

    // The actual generators to be calculated based on their traces.
    transform a;
    transform b;
    transform A;
    transform B;

    // Construct the generating transformationa a, b, A, and B.
    grandmaRecipe(vec4(tracea, 0, 0), vec4(traceb, 0, 0), a, b, A, B);

    // We're specifically interested in the properties of the fixed points of the commutators since they
    // give useful information about the Schottky curves we need to make the algorithm work.
    transform comAB = commutator(A,B);
    transform comAb = commutator(A,b);

    vec4 comabf1, comabf2, comABf1, comABf2, comaBf1, comaBf2, comAbf1, comAbf2;
    fixedPoints(comAB,comABf1,comABf2);
    fixedPoints(comAb,comAbf1,comAbf2);
    // No need to calculate these two, since they're always the same for this family of groups.
    comabf1 = vec4(1,0,0,0);
    comaBf1 = vec4(-1,0,0,0);

    vec4 fa1, fb1, fA1, fB1, fa2, fb2, fA2, fB2;
    fixedPoints(a,fa1,fa2);
    fixedPoints(b,fb1,fb2);
    fixedPoints(A,fA1,fA2);
    fixedPoints(B,fB1,fB2);

    vec3 col = vec3(1);

    const int maxIter = 30;
    float scale = 1.0;
    float de = 1.0;

    // Stores the z value whenever we apply transformation b.
    // If we're at the same position the next time we apply this transformation, we know that the orbit
    // has become periodic and can break out of the main loop.
    vec2 lastb = vec2(-10000);

#ifdef DEBUG_VIEW
    col *= 0.5;

    // Triangles used as a base for estimating the Schottky curves. Two vertices are placed
    // at the fixed points of the commutators, which are always points where the Schottky
    // curves touch. The third vertex is placed at a fixed point of the inverse transformation,
    // which will be the "tip" of the limit set.
    // There are some really obvious ways to make the estimate much more accurate that I'll get to
    // eventually.
    col.g += 0.5*(1.0-min(1.0, (0.5/zoom)*iResolution.y*abs(sdTriangle(z, fA1.xy, comABf1.xy, comAbf1.xy))));
    col.rg += 0.5*(1.0-min(1.0, (0.5/zoom)*iResolution.y*abs(sdTriangle(z, fB1.xy, comABf1.xy, comaBf1.xy))));
    col.b += 0.5*(1.0-min(1.0, (0.5/zoom)*iResolution.y*abs(sdTriangle(z, fb1.xy, comabf1.xy, comAbf1.xy))));
    col.r += 0.5*(1.0-min(1.0, (0.5/zoom)*iResolution.y*abs(sdTriangle(-z, fA1.xy, comABf1.xy, comAbf1.xy))));
    col.r += 0.5*(1.0-min(1.0, (0.5/zoom)*iResolution.y*abs(sdTriangle(-z, fB1.xy, comABf1.xy, comaBf1.xy))));
    col.r += 0.5*(1.0-min(1.0, (0.5/zoom)*iResolution.y*abs(sdTriangle(-z, fb1.xy, comabf1.xy, comAbf1.xy))));

    // Final (estimated) Schottky regions determined by choosing the closest triangle to a given point.
    // You can observe (at least in cases where the Schottky regions are valid) that each colored region
    // contains a distorted copy of all of the other colored regions, wich is the key to why this works.
    if(sdTriangle(z, fA1.xy, comABf1.xy, comAbf1.xy) < sdTriangle(z, fB1.xy, comABf1.xy, comaBf1.xy) 
        && sdTriangle(z, fA1.xy, comABf1.xy, comAbf1.xy) < sdTriangle(z, fb1.xy, comabf1.xy, comAbf1.xy) 
        && sdTriangle(z, fA1.xy, comABf1.xy, comAbf1.xy) < sdTriangle(-z, fA1.xy, comABf1.xy, comAbf1.xy) 
        && sdTriangle(z, fA1.xy, comABf1.xy, comAbf1.xy) < sdTriangle(-z, fB1.xy, comABf1.xy, comaBf1.xy) 
        && sdTriangle(z, fA1.xy, comABf1.xy, comAbf1.xy) < sdTriangle(-z, fb1.xy, comabf1.xy, comAbf1.xy))
    {
        col.g += 0.1;
    }
    else if((z.x >= comaBf1.x
        && sdTriangle(z, fB1.xy, comABf1.xy, comaBf1.xy) < sdTriangle(z, fb1.xy, comabf1.xy, comAbf1.xy) 
        && sdTriangle(z, fB1.xy, comABf1.xy, comaBf1.xy) < sdTriangle(-z, fA1.xy, comABf1.xy, comAbf1.xy) 
        && sdTriangle(z, fB1.xy, comABf1.xy, comaBf1.xy) < sdTriangle(-z, fB1.xy, comABf1.xy, comaBf1.xy) 
        && sdTriangle(z, fB1.xy, comABf1.xy, comaBf1.xy) < sdTriangle(-z, fb1.xy, comabf1.xy, comAbf1.xy))
        || (z.x < comaBf1.x && z.y < comaBf1.y))
    {
        col.rg += 0.1;
    }
    else if((z.x <= comabf1.x
        && sdTriangle(z, fb1.xy, comabf1.xy, comAbf1.xy) < sdTriangle(-z, fA1.xy, comABf1.xy, comAbf1.xy) 
        && sdTriangle(z, fb1.xy, comabf1.xy, comAbf1.xy) < sdTriangle(-z, fB1.xy, comABf1.xy, comaBf1.xy) 
        && sdTriangle(z, fb1.xy, comabf1.xy, comAbf1.xy) < sdTriangle(-z, fb1.xy, comabf1.xy, comAbf1.xy))
        || (z.x > comabf1.x && z.y < comabf1.y))
    {
        col.b += 0.1;
    }
    else
    {
        col.r += 0.1;
    }
#endif

    // The actual "escape time" algorithm based on the regions above.
    for(int i = 0; i < maxIter; i++)
    {
        // Add the known limit set points to distance estimate. 
        // By definition, they'll remain in the limit set after each transformation is applied.
        de = min(de, scale*min(min(distance(z, fB1.xy), distance(z, comABf1.xy)),distance(z, comaBf1.xy)));
        de = min(de, scale*min(min(distance(z, fb1.xy), distance(z, comabf1.xy)),distance(z, comAbf1.xy)));
        de = min(de, scale*min(distance(z, fA1.xy), distance(z, -fA1.xy)));
        de = min(de, scale*min(distance(z, -fB1.xy), distance(z, -comABf1.xy)));
        de = min(de, scale*min(distance(z, -fb1.xy), distance(z, -comAbf1.xy)));

        // Apply relevant transformation based on which Schottky region we're in.
        if(sdTriangle(z, fA1.xy, comABf1.xy, comAbf1.xy) < sdTriangle(z, fB1.xy, comABf1.xy, comaBf1.xy) 
            && sdTriangle(z, fA1.xy, comABf1.xy, comAbf1.xy) < sdTriangle(z, fb1.xy, comabf1.xy, comAbf1.xy) 
            && sdTriangle(z, fA1.xy, comABf1.xy, comAbf1.xy) < sdTriangle(-z, fA1.xy, comABf1.xy, comAbf1.xy) 
            && sdTriangle(z, fA1.xy, comABf1.xy, comAbf1.xy) < sdTriangle(-z, fB1.xy, comABf1.xy, comaBf1.xy) 
            && sdTriangle(z, fA1.xy, comABf1.xy, comAbf1.xy) < sdTriangle(-z, fb1.xy, comabf1.xy, comAbf1.xy))
        {
            scale /= clamp(jacobian(a, vec4(z,0,0)),-40.0,40.0);
            z = applyTransform(a, vec4(z,0,0)).xy;
        }
        else if((z.x >= comaBf1.x
            && sdTriangle(z, fB1.xy, comABf1.xy, comaBf1.xy) < sdTriangle(z, fb1.xy, comabf1.xy, comAbf1.xy) 
            && sdTriangle(z, fB1.xy, comABf1.xy, comaBf1.xy) < sdTriangle(-z, fA1.xy, comABf1.xy, comAbf1.xy) 
            && sdTriangle(z, fB1.xy, comABf1.xy, comaBf1.xy) < sdTriangle(-z, fB1.xy, comABf1.xy, comaBf1.xy) 
            && sdTriangle(z, fB1.xy, comABf1.xy, comaBf1.xy) < sdTriangle(-z, fb1.xy, comabf1.xy, comAbf1.xy))
            || (z.x < comaBf1.x && z.y < comaBf1.y))
        {
            if(distance(z, lastb) < 0.00001)
            {
                // Orbit has become periodic.
                break;
            }
            lastb = z;
            scale /= clamp(jacobian(b, vec4(z,0,0)),-40.0,40.0);
            z = applyTransform(b, vec4(z,0,0)).xy;
        }
        else if((z.x <= comabf1.x
            && sdTriangle(z, fb1.xy, comabf1.xy, comAbf1.xy) < sdTriangle(-z, fA1.xy, comABf1.xy, comAbf1.xy) 
            && sdTriangle(z, fb1.xy, comabf1.xy, comAbf1.xy) < sdTriangle(-z, fB1.xy, comABf1.xy, comaBf1.xy) 
            && sdTriangle(z, fb1.xy, comabf1.xy, comAbf1.xy) < sdTriangle(-z, fb1.xy, comabf1.xy, comAbf1.xy))
            || (z.x > comabf1.x && z.y < comabf1.y))
        {
            scale /= clamp(jacobian(B, vec4(z,0,0)),-40.0,40.0);
            z = applyTransform(B, vec4(z,0,0)).xy;
        }
        else
        {
            // Just exploiting that "Grandma's" groups are defined in such a way that they all have 180 degree
            // rotational symmetry, so we don't need to do anything about the upper Schottky region directly.
            // Applying the A transformation would also work, but it's slower to calculate and introduces more
            // distortion into the distance estimate.
            z = -z;
        }
    }

    col *= min(1.0, (0.5/zoom)*iResolution.y*de);

    fragColor = vec4(col, 0);
}

common_code:
// Mobius transformation (az+b)/(cz+d) with quaternion a, b, c, d
// Quaternions are used only to allow the transformations to be applied to 3D points for a future version of 
// this algorithm. As such, most of the operations aren't guaranteed to work in general but should be correct 
// for complex valued transformations.
// Thanks to https://www.shadertoy.com/view/MddfD7 for making me realize that this actually does "just work"
// to apply mobius transformations to points in 3D space.
struct transform
{
    vec4 a;
	vec4 b;
	vec4 c;
	vec4 d;
};

vec4 qconj(in vec4 q)
{
    return vec4(q.x, -q.yzw);
}

vec4 qinv(in vec4 q)
{
 	return qconj(q) / dot(q,q);
}

vec4 qmul(in vec4 p, in vec4 q)
{
    return vec4(p.x*q.x - p.y*q.y - p.z*q.z - p.w*q.w,
                p.x*q.y + p.y*q.x + p.z*q.w - p.w*q.z,
                p.x*q.z - p.y*q.w + p.z*q.x + p.w*q.y,
                p.x*q.w + p.y*q.z - p.z*q.y + p.w*q.x);
}

vec4 qdiv(in vec4 p, in vec4 q)
{
    return qmul(p, qinv(q));
}

vec4 qmobius(in vec4 a, in vec4 b, in vec4 c, in vec4 d, in vec4 z)
{ 
    return qdiv(qmul(a,z) + b, qmul(c,z) + d);
}

vec2 cmul(in vec2 za, in vec2 zb)
{
    return za*mat2(zb.x, -zb.y, zb.yx);
}

vec2 cinv(in vec2 z) 
{
    return z*vec2(1, -1)/dot(z,z);
}

vec2 cdiv(in vec2 za, in vec2 zb)
{
    return cmul(za, cinv(zb));
}

vec2 cexp(in vec2 z) 
{
    return exp(z.x)*vec2(cos(z.y), sin(z.y));
}

vec2 clog(in vec2 z) 
{
    return vec2(log(length(z)), atan(z.y,z.x));
}

vec2 cpow(in vec2 z, in float n)
{
    return cexp(n*clog(z));
}

vec2 csqrt(in vec2 z)
{
    return cpow(z, 0.5);
}

vec4 applyTransform(in transform t, in vec4 z)
{
    vec4 res = qmobius(t.a, t.b, t.c, t.d, z);
    return res;
}

transform composeTransform(in transform a, in transform b)
{
    transform res;

    res.a = qmul(b.a, a.a) + qmul(b.b, a.c);
    res.b = qmul(b.a, a.b) + qmul(b.b, a.d);
    res.c = qmul(b.c, a.a) + qmul(b.d, a.c);
    res.d = qmul(b.d, a.d) + qmul(b.c, a.b);

    return res;
}

transform invertTransform(in transform t)
{
    transform res;

    res.a = t.d;
    res.b = -t.b;
    res.c = -t.c;
    res.d = t.a;

    return res;
}

transform commutator(in transform a, in transform b)
{
    transform A = invertTransform(a);
    transform B = invertTransform(b);
    transform res = composeTransform(A, composeTransform(B, composeTransform(a, b)));

    return res;
}

vec4 discriminant(in transform t)
{
    return qmul(t.a+t.d, t.a+t.d)-qmul(vec4(4,0,0,0),qmul(t.a,t.d)-qmul(t.b,t.c));
}

void fixedPoints(in transform t, out vec4 f1, out vec4 f2)
{
    f1 = qdiv((t.a-t.d)+vec4(csqrt(discriminant(t).xy),0,0), qmul(vec4(2,0,0,0), t.c));
    f2 = qdiv((t.a-t.d)-vec4(csqrt(discriminant(t).xy),0,0), qmul(vec4(2,0,0,0), t.c));
}

float jacobian(in transform t, in vec4 z)
{
    vec4 denom = qmul(t.c, z) + t.d;
    return length(qdiv(qmul(t.a,t.d)-qmul(t.b,t.c), qmul(denom, denom)));
}

// https://www.shadertoy.com/view/XsXSz4
// ...with some added clamping as an ad hoc correction for some sloppiness I've introduced
float sdTriangle( in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2 )
{
	vec2 e0 = p1 - p0;
	vec2 e1 = p2 - p1;
	vec2 e2 = p0 - p2;

	vec2 v0 = p - p0;
	vec2 v1 = p - p1;
	vec2 v2 = p - p2;

	vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.00001, 0.9999 );
	vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.00001, 0.9999 );
	vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.00001, 0.9999 );

    float s = e0.x*e2.y - e0.y*e2.x;
    vec2 d = min( min( vec2( dot( pq0, pq0 ), s*(v0.x*e0.y-v0.y*e0.x) ),
                       vec2( dot( pq1, pq1 ), s*(v1.x*e1.y-v1.y*e1.x) )),
                       vec2( dot( pq2, pq2 ), s*(v2.x*e2.y-v2.y*e2.x) ));

	return -sqrt(d.x)*sign(d.y);
}

// From Indra's Pearls by David Mumford, Caroline Series, and David Wright p. 229
void grandmaRecipe(in vec4 ta, in vec4 tb, out transform a, out transform b, out transform A, out transform B)
{
    vec4 tmp = qmul(qmul(tb, ta), vec4(0.5,0,0,0));
    vec4 tab = tmp-qmul(vec4(0,1,0,0),vec4(csqrt(-(qmul(tmp,tmp)-qmul(ta,ta)-qmul(tb,tb)).xy),0,0));

    vec4 z0 = qdiv(qmul(tab - vec4(2,0,0,0),tb), qmul(tb,tab) - qmul(vec4(2,0,0,0),ta) + qmul(vec4(0,2,0,0), tab));
    a.a = qmul(ta, vec4(0.5,0,0,0));
    a.b = qdiv(qmul(ta, tab) - qmul(tb, vec4(2,0,0,0)) + vec4(0,4,0,0), qmul(qmul(tab, vec4(2,0,0,0)) + vec4(4,0,0,0), z0 ));
    a.c = qdiv(qmul(qmul(ta, tab) - qmul(tb, vec4(2,0,0,0)) - vec4(0,4,0,0), z0), qmul(tab, vec4(2,0,0,0)) - vec4(4,0,0,0));
    a.d = qmul(ta, vec4(0.5,0,0,0));

    b.a = qmul(tb - vec4(0,2,0,0), vec4(0.5,0,0,0));
    b.b = qmul(tb, vec4(0.5,0,0,0));
    b.c = qmul(tb, vec4(0.5,0,0,0));
    b.d = qmul(tb + vec4(0,2,0,0), vec4(0.5,0,0,0));

