vec2 rayCast(in vec3 ro, in vec3 rd, in int steps){
    float t = 0.0;
    float mate = -1.0;
    for(int i=0;i<steps;i++){
        vec2 d = map(ro + t*rd);
        mate = d.y;
        if(d.x < 0.0001 || t > 1000.0) break;
        t += d.x;
    }
    if(t > 1000.0) mate = -1.0;
    return vec2(t, mate);
}

