}




mat3 rotateX(float a){
    float c = cos(a);
    float s = sin(a);
    return mat3(
        1,0,0,
        0,c,s,
        0,-s,c
    );
}

mat3 rotateY(float a){
    float c = cos(a);
    float s = sin(a);
    return mat3(
        c,0,s,
        0,1,0,
        -s,0,c
    );
}

mat3 rotateZ(float a){
    float c = cos(a);
    float s = sin(a);
    return mat3(
        c,s,0,
        -s,c,0,
        0,0,1
    );
}

mat3 fromEuler(vec3 ro){
