////////////////////////////////////////////////////////////////////////////////
//
// Complex Cubic Equations, mla, 2022.
//
// Take three complex numbers as roots of a cubic equation, compute the equation,
// then solve and compare with the original roots. The solved roots are marked
// by rings and the contours show the roots of the equation directly.
//
// Not visually very exciting as the root finding is pretty much spot on (at
// least in the part of the complex plane we can see on the screen).

// Use mouse to drag roots around.
// 'a': hide axes
// 'r': rotation
// 'h': hide hud
// 's': clamp roots to real line
// 'n': apply final Newton-Raphson correction to all roots
// 'c': apply NR step to complex cube root calculation
//
////////////////////////////////////////////////////////////////////////////////

vec2 ccuberoot(vec2 z) {
  float t = carg(z)/3.0;
  vec2 u = vec2(cos(t),sin(t));
  float r = length(z);
  z /= r;
  if (key(CHAR_C)) {
    vec2 u2 = cmul(u,u);
    u -= cdiv(cmul(u,u2)-z,3.0*u2);
  }
  return pow(r,1.0/3.0)*u;
}

mat3x2 solvecubic(vec2 p, vec2 q) {
  // Solve depressed cubic: x+3px+q = 0 - note factor of 3!
  // Put x = u+v and get x = u+v+3xuv
  // Substitute in original equation and get:
  // u+v+(3uv+3p)x + q = 0
  // Now let's constrain u,v so that uv+p = 0, and then:
  // uv = -p ie. uv = -p and u+v = -q
  // This gives a quadratic equation in t with roots u and v:
  // t+qt-p = 0 so try to solve as:
  // u = -q + sqrt(q+4p), v = -q - sqrt(q+4p)
  // and take cube roots to get solution x = u+v.
  //
  // HOWEVER, we can't just combine any cube roots of the
  // quadratic solutions, the condition uv+p = 0 above must also
  // hold, so what we do is take either one of the quadratic
  // solutions with its three cube roots as the three values
  // of u, and derive the corresponding v's directly as v = -p/u.
  // As with solving a real quadratic equation, it seems prudent
  // to use the quadratic root with the largest absolute magnitude,
  // and given one cube root, we can easily find the others by
  // multiplying by the cube roots of unity,  and .
  vec2 q2 = cmul(q,q);
  vec2 p3 = cmul(p,cmul(p,p));
  vec2 s = csqrt(q2+4.0*p3);
  float sign = dot(q,s) > 0.0 ? -1.0 : 1.0; // Get largest magnitude root
  vec2 u3 = 0.5*(-q+sign*s);
  vec2 u = ccuberoot(u3);
  vec2 u1 = cmul(u,omega); // Get other cuberoots by multiplying by  and .
  vec2 u2 = cmul(u,omega2);
  return mat3x2(u-cdiv(p,u), u1-cdiv(p,u1), u2-cdiv(p,u2));
}

mat3x2 solvecubic(vec2 B, vec2 C, vec2 D) {
  // Solve x+Bx+Cx+D = 0
  B /= 3.0; C /= 3.0;  // Simplifies sums
  vec2 B2 = cmul(B,B); // Depress with x->x+B
  // p = c-b*b
  vec2 p = C-B2;
  // q = 2.0*b*b*b - 3.0*b*c + d
  vec2 q = cmul(B,2.0*B2-3.0*C) + D;
  mat3x2 res = solvecubic(p,q);
  // Undepress by subtracting B
  return mat3x2(res[0]-B,res[1]-B,res[2]-B);
}

vec2 evalcubic(vec2 a, vec2 b, vec2 c, vec2 z) {
  //(x-a)(x-b)(x-c)
  vec2 res = z-a;
  res = cmul(res,z-b);
  res = cmul(res,z-c);
  return res;
}

float hud(vec2 fragCoord, mat3x2 rootsin, mat3x2 rootsout) {
  const int digits = 7;
  const int nrows = 15, ncols = 4;
  // Table structure
  vec2 cellsize = iResolution.xy/vec2(ncols,nrows);
  ivec2 cellindex = ivec2(fragCoord/cellsize);
  float fsize = cellsize.y; // "Font size" = line height in pixels
  int column = cellindex.x;
  int row = nrows-cellindex.y-1;
  vec2 p = mod(fragCoord,cellsize)/fsize;
  float x = 1.234;
  if (row > 2) return 0.0;
  for (int i = 0; i < 3; i++) {
    if (row == i) {
      if (column == 0) x = rootsin[i].x;
      if (column == 1) x = rootsin[i].y;
      if (column == 2) x = rootsout[i].x;
      if (column == 3) x = rootsout[i].y;
    }
  }
  // Draw the character
  vec4 metrics = vec4(0.25,0.75,0,1);
  float charwidth = metrics.y-metrics.x;
  float colwidth = cellsize.x/fsize-1.0; // Cell width in chars, with padding
  int charpos = int(p.x/charwidth);
  vec2 uv = vec2(0);
  int c = floatchar(x,digits,charpos); // Get character at charpos, 0 if out of range
  if (c < 0) return 0.0;
  float charstart = charwidth*float(charpos); // character start
  uv = char(vec2(p.x-charstart+metrics.x,p.y),c);
  float uvscale = 0.5/(16.0*fsize);   // 0.5 to sharpen a little
  vec2 dx = vec2(uvscale,0), dy = vec2(0,uvscale);
  vec4 t = textureGrad(iChannel0,uv,dx,dy);
  return t.x;  
}

mat3x2 sort(mat3x2 a) {
  vec2 tmp;
  if (a[0].x > a[1].x) tmp = a[0], a[0] = a[1], a[1] = tmp;
  if (a[1].x > a[2].x) tmp = a[1], a[1] = a[2], a[2] = tmp;
  if (a[0].x > a[1].x) tmp = a[0], a[0] = a[1], a[1] = tmp;
  return a;
}

float showkeys(vec2 fragCoord) {
  float K = 30.0;
  if (fragCoord.y > K) return 0.0;
  int box = int(fragCoord.x/K);
  if (box >= 2) return 0.0;
  if (box == 0 && !key(CHAR_N)) return 0.0;
  if (box == 1 && !key(CHAR_C)) return 0.0;
  vec2 boxcoord = mod(fragCoord,K)/K-0.5;
  float px = 0.5/K;
  return 1.0-smoothstep(-px,px,length(boxcoord)-0.4);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    setscale();
    vec2 z = map(fragCoord);
    vec2 a = getselection(0);
    vec2 b = getselection(1);
    vec2 c = getselection(2);
    if (key(CHAR_S)) a.y = b.y = c.y = 0.0; 
    vec3 col1 = vec3(1,1,0);
    vec3 col2 = vec3(0,1,1);
    vec3 neutral = vec3(1,0,0);

    vec2 w = evalcubic(a,b,c,z);
    if (!key(CHAR_R)) {
      float t = 0.25*iTime;
      w = cmul(w,vec2(cos(t),sin(t)));
    }
    w = clog(w);
    float alpha = w.y/PI;
    vec3 basecol = alpha < 0.0 ? col1 : col2;
    vec3 color = mix(neutral,basecol,abs(alpha));

    float px = fwidth(length(z));
    if (!key(CHAR_A)) {
      // Draw axes
      float eps = 0.05;
      float d = min(abs(z.x),abs(z.y));
      float x = z.x-round(z.x);
      float y = z.y-round(z.y);
      d = min(d,segment(vec2(x,z.y),vec2(0,-eps),vec2(0,eps)));
      d = min(d,segment(vec2(z.x,y),vec2(-eps,0),vec2(eps,0)));
      color = mix(vec3(0),color,smoothstep(0.0,px,d));
    }
    float t = 0.5+atan(w.x)/PI;
    vec2 uv = 10.0*w/PI;
    float px2 = fwidth(uv.x);// fwidth for function result
    uv = fract(uv);
    uv = min(uv,1.0-uv);
    color = mix(vec3(t),color,smoothstep(0.0,px2,min(uv.x,uv.y)));
    // Now let's solve the cubic equation and see if we get the
    // original roots back.
    // x+Ax+Bx+C = 0
    vec2 A = -(a+b+c);
    vec2 B = cmul(a,b)+cmul(b,c)+cmul(c,a);
    vec2 C = -cmul(a,cmul(b,c));
    mat3x2 roots = solvecubic(A,B,C);
    if (key(CHAR_N)) {
     for (int i = 0; i < 3; i++) {
        vec2 z = roots[i];
        z -= cdiv(C+cmul(z,B+cmul(z,A+z)),B+cmul(z,2.0*A+3.0*z));
        roots[i] = z;
      }
    }
    roots = sort(roots);
    for (int i = 0; i < 3; i++) {
      vec2 w = roots[i];
      // Draw a little circle around each root.
      color *= smoothstep(0.0,px,abs(distance(w,z)-0.05));
    }
    if (!key(CHAR_H)) {
      color = mix(color,vec3(0),hud(fragCoord,sort(mat3x2(a,b,c)),roots));
      color = mix(color,vec3(0),showkeys(fragCoord));
    }

    color = pow(color,vec3(0.4545));

    // Output to screen
    fragColor = vec4(color,1.0);
}

