const float PI = 3.14159265;
const float TWOPI = 2.0*PI;

float segment(vec2 p, vec2 a, vec2 b) {
  p -= a; b -= a;
  float h = dot(p,b)/dot(b,b);
  h = clamp(h, 0.0, 1.0);
  return length(p-b*h);
}

vec2 cmul(vec2 z, vec2 w) {
  return vec2(z.x*w.x-z.y*w.y,z.x*w.y+z.y*w.x);
}

vec2 cinv(vec2 z) {
  return vec2(z.x,-z.y)/dot(z,z);
}

vec2 cdiv(vec2 z1, vec2 z2) {
  return cmul(z1,cinv(z2));
}

vec2 clog(vec2 z) {
  float r2 = dot(z,z);
  return vec2(0.5*log(r2),atan(z.y,z.x));
}

float carg(vec2 z) {
  return atan(z.y,z.x);
}

vec2 csqrt(vec2 z) {
  float t = carg(z);
  return sqrt(length(z))*vec2(cos(0.5*t),sin(0.5*t));
}

vec2 ccbrt0(vec2 z) {
  // Trigless Newton Raphson verson
  float r = length(z);
  z /= r; // Normalize z
  vec2 u = z+vec2(1,0); // First approximation (when normalized)
  for (int i = 0; i < 4; i++) {
    u = normalize(u);
    vec2 u2 = cmul(u,u);
    u -= cdiv(cmul(u,u2)-z,3.0*u2);
  }
  return pow(r,1.0/3.0)*u;
}

// cuberoots of 1.
