        p.xz += .2 - .1*i + sin(p.y*.2) + .2*exp(.01+p.z*.2);
        p.z -= .2*sin(p.z*.5*i/iter*2.*PI);
        p -= .2;

    }

    return clamp(p, -1e5, 1e5);

