#define fmod(x,y) floor(max(mod(floor(-(x))*4.,y),mod(floor(x)*3.,y)))
#define fmod1(x,y) floor(min(mod(floor(-(x))*4.,y),mod(floor(x)*3.,y)))
vec2 mainSound(int samp, float time){  
  float
  s1 = pow(2.,1.+fmod(time*8.,3.)),
  s2 = pow(2.,1.+fmod1(time*8.,3.)),
  m1 = s1*(1.+fmod(time/s2/s1+1.,2.)),
  t = time/8.,
  m4 = fmod1(t*s1/(1.+fmod(t/s1,2.)),s2),
  m3 = fmod((t+m4)*s1/m1,s2);
  t *= s1*s1/(1.+fmod(t/s1,2.));
  float m2 = 1. + fmod(t/s1,s1);
  float a = 64.*pow((1.-sqrt(fract(t/s1)/m1))/4.,2.);
  float nb = time*pow(2.,(m3+m2)/5.+6.5);
  return abs(.5-vec2(fract(nb*.998),fract(nb)))*a;
}

