#define USE_NOISE 1

const vec2 anisotropicSmoothness = vec2(200, 1000);

vec2 mouse;

// Creates an abstract surface at the hit point with random geometry
// that can be used to simulate microfacet rendering.
HeightPlane getMicrofacetSurface(in Ray ray, in Hit hit, vec3 tangent, vec2 anisoSmoothness, vec2 fragCoord, inout uint rngState)
{
    HeightPlane hPlane;

    #if USE_NOISE != 0
        // Use blue noise in screen space to randomize
        // the abstract plane per pixel.
        //
        // We randomize the abstract surface because
        // we want to integrate the visibility of the surface
        // from this ray over the entire heightmap, not just a single point.
        // Adding per-pixel randomization helps create the appearance
        // of convergence earlier. (Swaps banding with noise).
        vec4 noise = sampleBlueNoise(iChannel3, fragCoord, rngState);

        // Randomize the uv per pixel so that the entire heightmap
        // is sampled.
        hPlane.uvOffset = noise.xy;
    #else
        // Use blue noise in screen space to randomize
        // the abstract plane per pixel.
        //
        // We randomize the abstract surface because
        // we want to integrate the visibility of the surface
        // from this ray over the entire heightmap, not just a single point.
        // Adding per-pixel randomization helps create the appearance
        // of convergence earlier. (Swaps banding with noise).
        vec4 noise = sampleBlueNoise(iChannel3, vec2(0), rngState);

        // Randomize the uv per pixel so that the entire heightmap
        // is sampled.
        hPlane.uvOffset = noise.xy;
    #endif

    // Plane is at the hit point.
    vec3 hPlanePos = getPoint(ray, hit);

    // Rotate the plane to point in the same direction as the hit point "hit",
    // with the plane's "up" direction pointing along the "tangent". 
    // This will define the direction of anisotropy.
    quaternion hPlaneRotation = LookRotation(hit.normal, tangent);

    // Scale does the following.
    //     x : stretches out the texels horizontally.
    //     y : stretches out the texels vertically.
    //     z : the depth variation in world units
    //
    // A surface can be made smoother by spreading out heightmap texels.
    // If you have different smoothness along the x and y, you can
    // get an anisotropic brdf.
    vec3 hPlaneScale = vec3
    (
        anisoSmoothness,
        1
    );

    hPlane.localToWorld = TRS(hPlanePos, hPlaneRotation, hPlaneScale);



    return hPlane;
}

vec3 scene(Ray r, in vec2 fragCoord, inout uint rngState)
{
    // need to add a nonzero value to the x or z here 
    // or else it doesn't draw the plane... wtf?
    Plane p = Plane(-1.0, vec3(0.001,1,0));
    Sphere s = Sphere(0.5, vec3(0));
    Hit sceneHit = intersectSphere(s, r);

    swapIfCloser(sceneHit, intersectPlane(p, r));

    if(sceneHit.distance >= noHit.distance)
        return vec3(0.0);

    HeightPlane mfSurf = getMicrofacetSurface(r, sceneHit, vec3(0,1,-1), anisotropicSmoothness, fragCoord, rngState);

    Hit microHit = intersectHeightPlane(mfSurf, r, iChannel0, iChannelResolution[1].xy);

    Light light;
    vec2 m = mouse.xy / iResolution.xy;
    m = m * 2.0 - 1.0;
    m.x *= iResolution.x / iResolution.y;
    m.x = -m.x;
    quaternion lightRotation = FromAngleAxis(vec3
    (
        m.yx * vec2(Pi, Pi), 
        0
    ));

    light.dir = mul(lightRotation, vec3(0,0,-1));//normalize(vec3(iMouse.xy / iResolution.xy * 2.0 - 1.0, -0.2));
    light.color = vec3(100);

    // Microfacet self shadowing.
    {
        Ray lightRay;
        lightRay.direction = -light.dir;
        const float lightMfSurfDist = 100.0;
        lightRay.origin = getPoint(r, microHit) + light.dir * lightMfSurfDist;

        Hit lightMfHit = intersectHeightPlane(mfSurf, lightRay, iChannel0, iChannelResolution[1].xy);

        if(lightMfSurfDist-0.0001 > lightMfHit.distance)
        {
            light.color = vec3(0);
        }
    }

    // Fixed self shadowing, no longer needed!
    // EDIT: Still a weird edge case for stuff opposite to the light source.
    if(dot(light.dir, sceneHit.normal) <= 0.)
        light.color = vec3(0);


    Indirect ind;
    {
        ind.diffuse = vec3(0);
        ind.specular = vec3(0);
    }

    MaterialMetallic mat;
    {
        mat.albedo  = vec3(0.01, 0.1, 1.0);
        mat.smoothness = 1.0;
        mat.metallic = 0.9;
    }

    vec3 color = lighting(mat, microHit.normal, -r.direction, light, ind);
    return color;
}

Ray camTF(Ray r, in vec2 fragCoord, inout uint rngState)
{
    r.origin = vec3(0, 0, -1.25);

    /*quaternion q = EulerZXY(vec3
    (
        iMouse.yx / iResolution.yx * vec2(-Tau, Tau) - vec2(Pi, Pi), 
        0
    ));

    r.origin = mul(q, r.origin);
    r.direction = mul(q, r.direction);*/

    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iMouse.xy != vec2(0))
    {
        mouse = iMouse.xy;
    }
    else
    {
        mouse = iResolution.xy * vec2(0.4, 0.6);
    }

    // Write screen size to pixel 0,0
    {
        ivec2 iFragCoord = floorToInt(fragCoord);
        if(iFragCoord == ivec2(0,0))
        {
            fragColor = iResolution.xyxy;
            return;
        }
    }

    uint rngState = uint
    (
        uint(iFrame) * uint(1973) 
    ) | uint(1);

    // We're averaging samples together, so only render one sample per frame
    // with random offsets.
    vec2[1] msaaCoords;
    msaaCoords[0] = uniform01ToGauss(sampleBlueNoise(iChannel3, fragCoord, rngState).xy) * 1.0;

	vec3 color = vec3(0);
    render
    (
        color, 

        // Scene to render
        scene, 

        // Camera transform function
        camTF,

        fragCoord, 
        iResolution.xy, 

        // Zoom
        2.0,

        // MSAA coords and count
        msaaCoords, 
        1,

        rngState
    );

	fragColor = vec4(color,1.0);

    bool usePreviousSamples = true;
    {
        // Mouse changed scene?
        if(iMouse.z > 0.5)
            usePreviousSamples = false;

        // Screen size changed?
        else if(texelFetch(iChannel1, ivec2(0), 0).rg != iResolution.xy)
            usePreviousSamples = false;
    }

    if(usePreviousSamples)
        fragColor += texture(iChannel1, fragCoord / iResolution.xy);
}

