const int nrows = 25, ncols = 7, digits = 7;
int gethudpos(vec2 fragCoord) {
  // Compute where this pixel is in the hud table
  vec2 cellsize = iResolution.xy/vec2(ncols,nrows);
  ivec2 cellindex = ivec2(fragCoord/cellsize);
  float fsize = cellsize.y; // "Font size" = line height in pixels
  int col = cellindex.x;
  int row = nrows-1-cellindex.y;
  return row*ncols+col;
}

float hud(vec2 fragCoord, float hudval) {
  // Write the hud value.
  vec2 cellsize = iResolution.xy/vec2(ncols,nrows);
  float fsize = cellsize.y; // "Font size" = line height in pixels
  // Get diffs for textureGrad
  float sharpness = 0.5;
  float uvscale = sharpness/16.0/fsize;
  vec2 dx = vec2(uvscale,0), dy = vec2(0,uvscale);
  //ivec2 cellindex = ivec2(fragCoord/cellsize);
  //int col = cellindex.x;
  //int row = cellindex.y;
  vec2 p = mod(fragCoord,cellsize)/fsize; // Cell coordinates
  // Draw the character
  vec4 metrics = vec4(0.25,0.75,0,1);
  float charwidth = metrics.y-metrics.x;
  float colwidth = cellsize.x/fsize-1.0; // Cell width in chars, with padding
  int charpos = int(p.x/charwidth);
  vec2 uv = vec2(0);
  int c = floatchar(hudval,digits,charpos); // Get character at charpos, 0 if out of range
  if (floor(hudval) == hudval) {
    int h = int(hudval);
    if (h%15 == 0) c = charpos >= 9 ? -1 : int[](32,70,73,90,90,66,85,90,90)[charpos];
    else if (h%3 ==0) c = charpos >= 5 ? -1 : int[](32,70,73,90,90)[charpos];
    else if (h%5 == 0) c = charpos >= 5 ? -1 : int[](32,66,85,90,90)[charpos];
  }
  if (c < 0) return 0.0;
  float charstart = charwidth*float(charpos); // character start
  uv = char(vec2(p.x-charstart+metrics.x,p.y),c);
  vec4 t = textureGrad(iChannel3,uv,dx,dy);
  return t.x;  
}

int hudpos = -1;        // The hud position this pixel is in
int hudwritepos = 0;    // Position being written to
float hudval;           // The value to write
bool gothudval = false; // Indicate if a value has been written

void print(float x) {
  if (hudpos < 0 || hudpos != hudwritepos++) return;
  gothudval = true;
  hudval = x;
  if (key(CHAR_Z) && (hudpos%3 == 0 || hudpos%5 == 0)) hudval = float(hudpos);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  fragColor = mainimage(fragCoord);
  //return; // Uncomment to compare version without debug
  vec2 mouse = 0.5*iResolution.xy;
  if (iMouse.x > 0.0) mouse = iMouse.xy;
  hudpos = gethudpos(fragCoord);
  // Rerun shader with constant coords
  mainimage(mouse);
  // And display any debug output.
  if (gothudval) {
    float h = hud(fragCoord,hudval);
    fragColor.xyz = mix(fragColor.xyz,vec3(1),h);
  }
  if (alert) fragColor.xyz = 1.0-fragColor.xyz;
}

