// Made by StoneTrench

int seedSize = 16;

float smoothStep(float x){
    float x3 = x * x * x;
    return 6.0 * (x3 * x * x) - 15.0 * (x3 * x) + 10.0 * (x3);
}
float lerp(float a, float b, float t){
    return a + t * (b - a);
}


float sampleSeed(float _x, float _y){
    vec2 pF = mod(abs(vec2(_x, _y)), float(seedSize));

    int x = int(pF.x);
    int y = int(pF.y);

    return seed[x + (y * seedSize)] / 10.0f;
}

float sampleNoise(vec2 p){
    float scale = 100.0f;
    vec2 s = p / scale;
    vec2 f = floor(s);
    vec2 c = s;

    float s0 = sampleSeed(f.x, f.y);
    float s1 = sampleSeed(f.x + 1.0f, f.y);
    float s2 = sampleSeed(f.x, f.y + 1.0f);
    float s3 = sampleSeed(f.x + 1.0f, f.y + 1.0f);

    float lx = lerp(s0, s1, (c.x - f.x));
    float ly = lerp(s2, s3, (c.x - f.x));

    return lerp(lx, ly, (c.y - f.y));
}

float Octaves(vec2 fragCoord, int octaves){
    float result = 0.0;

    float b = 1.0;
    for (int i = 0; i < octaves; i++){
        b *= 2.0;
        result += sampleNoise(b * fragCoord);
    }

    return sin(result / float(octaves));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float r = 100.0;
    float t = iTime / r * 100.0;
    float noise = abs(Octaves(fragCoord + vec2(sin(t), cos(t)) * r, 4));

    fragColor = vec4(1.0) * noise;
}

vec2 Path(vec2 x) {
    vec2 p = floor(x);
    vec2 f = fract(x);
	f = f*f*(3.-2.*f);
	return textureLod(iChannel2, (p+vec2(37.5,17.5) + f)/256.0, 0.).xy;
}

#define S(x) vec4(Path(x*vec2(.04,.1))*vec2(6,9),0,0)

void hellCarting( out vec4 o, in vec2 C, inout TextCursor textCur ) 
{
    vec4 p = vec4(C.xy,0,0)/iResolution.xyxy-.5, d=p, t, c, s;
    p.z += iTime*8.;
    p -= S(p.z);
    float x, r, w, i;
    for(i=1.5; i>0.; i-=.01)
    {
        // Select one of 5 type of wood beam separation
        vec2 z = (p.zz-2.)*.25;
        z = Path(z - fract(z))*4.+.5;
        z = (z - fract(z))/10.;
        z = mix(z, vec2(1.), step(vec2(.4), z));      
        z *= step(4., mod(p.z-2., 8.));
		float f = mix(z.x, z.y, step(p.x, 0.));                

        t = abs(mod(c=p+S(p.z), 8.)-4.);
        w = step(0., c.y);
        r = (step(2.6, t.x) - step(2.8, t.x)) * w;        
        s = texture(iChannel0, (c.y*t.x > 3. ? t.zxy:t.yzx)-3.);
        x = min(t.x + f, t.y)-s.x * (1.-r) - r*.8;  

        // Hacky way of doing word wrapping.
        {
            TextCursor temp = textCur;             
            printFloat(x, temp);   
            if(temp.coord.y != textCur.coord.y)
            {
                printChar(cNl, textCur);       
            }
        }
        printFloat(x, textCur);
        printChar(cSp, textCur);

        if(x < .01) break;
        p -= d*x*.5;
     }
    w = step(t.x, 0.8) * step(2.8, t.z) + step(2.8, t.x) * step(0., c.y);
	o = p.wyyw*.02 + 1.3* mix(s, vec4(.8), r) * mix(vec4(1), vec4(1,.5,.2,1), w ) * i/p.w;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Draw the scene normally.
    TextCursor tDummy;
    hellCarting(fragColor, fragCoord, tDummy);

    // Draw debugging info.
    {
        Font f;
        f.texSize = iChannelResolution[3].xy;
        f.charSize = iChannelResolution[3].xy / vec2(16, 16);

        TextCursor t;
        const vec2 charScale = vec2(15, 15);
        t.startPos = vec2(0, iResolution.y);
        t.coord = ivec2(0);
        t.font = f;
        t.color = vec4(1,1,0,1);
        t.charSize = vec2(1,1) * charScale;
        t.charSpace = vec2(1, 1) * charScale;
        t.pageWidth = int(iResolution.x / t.charSize.x);
        t.fragCoord = fragCoord;

        vec4 colorDummy;
        hellCarting(colorDummy, iMouse.xy, t);
        drawText(t, iChannel3, fragColor);
    }
}

