struct Font
{
    vec2 texSize;
    vec2 charSize;
};

struct TextCursor
{
    vec2 startPos;
    ivec2 coord;
    Font font;
    vec4 color;
    vec2 charSize;
    vec2 charSpace;
    int pageWidth;
    vec2 fragCoord;
    vec2 outUV;
    vec4 outColor;
};

vec4 getFontCharRect(in Font f, ivec2 char)
{
    vec2 uvSize = f.charSize / f.texSize;
    return vec4(char, char+ivec2(1)) * uvSize.xyxy;
}

vec4 getCharScreenRect(in TextCursor t)
{
    vec2 leftTop = t.startPos + t.charSpace * vec2(t.coord.x, -t.coord.y);
    vec2 rightBottom = leftTop + t.charSize * vec2(1, -1);

    return vec4(leftTop.x, rightBottom.y, rightBottom.x, leftTop.y);
}

void updateOutUV(ivec2 char, inout TextCursor t)
{
    vec4 fontRect = getFontCharRect(t.font, char);
    vec4 screenRect = getCharScreenRect(t);
    vec2 screenRectUV = (t.fragCoord - screenRect.xy) / (screenRect.zw - screenRect.xy);

    if(clamp(screenRectUV, vec2(0), vec2(1)) != screenRectUV)
    {
        return;
    }

    t.outUV = screenRectUV * (fontRect.zw - fontRect.xy) + fontRect.xy;
    t.outColor = t.color;
}

void blend(vec4 src, inout vec4 dest)
{
    dest.rgb = mix(dest.rgb, src.rgb, src.a);
    dest.a = mix(dest.a, 1.0, src.a);
}

void incrementCursor(inout TextCursor t)
{
    t.coord.x += 1;
    if(t.coord.x >= t.pageWidth)
    {
        t.coord.x = 0;
        t.coord.y += 1;
    }
}

void newlineCursor(inout TextCursor t)
{
    t.coord.x = 0;
    t.coord.y += 1;
}

void printChar(ivec2 char, inout TextCursor t)
{
    if(char == cNl)
    {
        newlineCursor(t);
        return;
    }

    updateOutUV(char, t);
    incrementCursor(t);
}

void printString(String s, inout TextCursor t)
{
    for(int i = 0; i < min(s.count, maxStringLength); i++)
    {
        printChar(s.chars[i], t);
    }
}

void printLine(String s, inout TextCursor t)
{
    for(int i = 0; i < min(s.count, maxStringLength); i++)
    {
        printChar(s.chars[i], t);
    }
    printChar(cNl, t);
}

void printInt(int val, inout TextCursor t)
{
    if(val < 0)
    {
        printChar(cMinus, t);
        val = -val;
    }

    int[maxStringLength] digits;
    int count = 0;

    for(;;)
    {
        digits[count++] = val % 10;

        if(count >= maxStringLength)
            break;

        val /= 10;

        if(val == 0)
            break;
    }

    for(int i = count-1; i >= 0; i--)
    {  
        printChar(Digits[digits[i]], t);
    } 
}

void printFloat(float val, inout TextCursor t)
{
    if(val < 0.0)
    {
        printChar(cMinus, t);
        val = -val;
    }

    int[maxStringLength] digits;
    int count = 0;

    int iVal = int(val);
    val -= float(iVal);

    for(;;)
    {
        digits[count++] = iVal % 10;

        if(count >= maxStringLength)
            break;

        iVal /= 10;

        if(iVal == 0)
            break;
    }

    for(int i = count-1; i >= 0; i--)
    {  
        printChar(Digits[digits[i]], t);
    } 

    printChar(cDot, t);

    const int maxDecimalCount = 4;
    for(int i = 0; i < maxDecimalCount; i++)
    {
        val *= 10.0;

        int digit = int(val);
        val -= float(digit);

        printChar(Digits[digit], t);

        if(val <= 0.0)
            break;
    }
}

void drawText(TextCursor t, sampler2D fontTexture, inout vec4 fragColor)
{
    vec4 color = t.outColor;
    color.a *= texture(fontTexture, t.outUV).r;
    blend(color, fragColor);
}

