/////////////////////////////////////////////////////////////
//
// The Waste Land, mla, 2022 (with help from T.S. Eliot)
//
// Simple but efficient scrolling text display.
//
// Text from Project Gutenberg.
//
/////////////////////////////////////////////////////////////

const int nlines = lines.length();

int getchar(int col, int row) {
  if (row < 0 || row >= nlines) return -1;
  //return col>=64?-1:row%4*64+col%64; // Debug
  int linestart = lines[row];
  int lineend = lines[row+1];
  int index = linestart+col;
  if (index >= lineend) return -1;
  uvec4 v = data[index/16];
  index %= 16;
  uint t = v[index/4];
  index %= 4;
  t >>= ((3-index)*8);
  return int(t)&0xff;
}

// https://www.shadertoy.com/view/4sBfRd
vec2 char(vec2 p, int c) {
  // Compute uv for font texture.
  // p is [0,1] coordinates in square character
  return fract((vec2(c,15-c/16)+p)/16.0);
}

float hud(vec2 fragCoord) {
  vec4 metrics = vec4(0.25,0.75,0,1); // Region of font to display, z & w ignored
  float charwidth = metrics.y-metrics.x;
  float ncols = 78.0;

  fragCoord.y = iResolution.y-fragCoord.y; // Flip vertically
  float fsize = iResolution.x/(charwidth*ncols); // 'Font size' = line height in pixels
  vec2 uv = fragCoord / fsize; 
  float scrollspeed = 0.75;
  if (iMouse.x > 0.0) scrollspeed = 2.0*iMouse.y/iResolution.y;
  uv.y += scrollspeed*max(0.0,iTime-2.0);
  if (iMouse.x > 0.0) uv.y += iMouse.x/iResolution.x*(float(nlines)-iResolution.y/fsize);

  int row = int(floor(uv.y));
  int col = int(floor(uv.x/charwidth));
  int c = getchar(col,row); // Get character at charpos, 0 if out of range
  if (c < 0) return 0.0;
  float x = uv.x-charwidth*float(col)+metrics.x;
  float y = fract(-uv.y);
  vec2 tuv = char(vec2(x,y),c);
  // diffs for textureGrad 
  float uvscale = 0.5/(16.0*fsize);   // 0.5 to sharpen a little
  vec2 dx = vec2(uvscale,0), dy = vec2(0,uvscale);
  vec4 t = textureGrad(iChannel0,tuv,dx,dy);
  return t.x;  
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec3 col = vec3(hud(fragCoord));
  fragColor = vec4(col,1.0);
}

const float n_tau = 6.283185;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
