    float n_angle_nor = (atan( o_pix_coo_nor.x , o_pix_coo_nor.y)+(n_tau/2.))/n_tau;

    float n_dist_center = length(o_pix_coo_nor);

    float n_dist_angle = smoothstep(n_dist_center, n_dist_center, n_angle_nor);

    float n_t_yea = floor(iDate.x);//year-1
    float n_t_mon = floor(iDate.y);//month-1
    float n_t_day = floor(iDate.z);//day-1
    float n_t_sec = floor(iDate.w);//secs since midnight
    float n_t_min_nor = fract(n_t_sec/60./60.);
    float n_t_hou = floor(n_t_sec/60./60.);
    float n_rot_nor = 0.0;
    float n_ang_sec_nor = mod(-(1./60.)*n_t_sec+n_rot_nor,1.);
    float n_ang_min_nor = mod(-n_t_min_nor+n_rot_nor,1.);
    float n_ang_hou_nor = mod(-(1./24.)*n_t_hou+n_rot_nor,1.);

    float n_radius_sec = min(0.5, n_dist_center);
    float n_radius_min = min(0.25, n_dist_center);
    float n_radius_hou = min(0.1, n_dist_center);

    float n_dist2_sec = length( o_pix_coo_nor - vec2(
        n_radius_sec * cos(n_ang_sec_nor*n_tau),
        n_radius_sec * sin(n_ang_sec_nor*n_tau)
    ));
    n_dist2_sec = sqrt(n_dist2_sec);
    float n_dist2_min = length( o_pix_coo_nor - vec2(
        n_radius_min * cos(n_ang_min_nor*n_tau),
        n_radius_min * sin(n_ang_min_nor*n_tau)
    ));
    n_dist2_min = sqrt(n_dist2_min);

    float n_dist2_hou = length( o_pix_coo_nor - vec2(
        n_radius_hou * cos(n_ang_hou_nor*n_tau),
        n_radius_hou * sin(n_ang_hou_nor*n_tau)
    ));

    n_dist2_hou = sqrt(n_dist2_hou);

    fragColor = vec4(
        n_dist2_sec*5.,
        n_dist2_min*5.,
        n_dist2_hou*5., 
        1.
    );


 }

// golfing 1439 chars https://shadertoy.com/view/ws23D3
