#define N          normalize

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = u+u - R;
    vec3  P = vec3(0, 6, 3.2),
          D = N( vec3(0,1,0) - P ), f;
      //  f = N(vec3(D.y,0,9));    // right
      //  D = N( D*3.5*R.y + U.x*f + U.y*cross(f,D) );
          D = N( D*3.5*R.y - U.xyy*vec3(1,-D.z,D.y) );
    float t = iTime,
          l = dot(R=D.xy,R),
          L = 36.,     // dot(P.xy,P.xy),
          o = 12.*D.y, // dot(P.xy,D.xy)*2.,
          b = ( P/D + o/l ).z /3., 
          c = ( L +o*P/D ).z /l, 
          p = b*b - c/3.,
          s = sqrt(abs(p)),
          q = b*c - ( L*P.z + 1. ) /l/D.z - 2.*b*b*b; // -

    if ( q*q/4. < p*p*p )                     // --- if 3 sol
        l = acos(q/2./s/s/s), 
        l = 2.*s* cos((l+round(1.5-l/6.283)*6.283)/3.);  // sol(k)
    else                                      // --- if 1 sol
        l = s+s,
        L = abs(q/p/l), q = sign(q),
        l *= p < 0. ? sinh(q*asinh(L)/3.) // - sinh(asinh(-q/p/-l)/3.) , fix asinh() symetry error 
                    : q*cosh(acosh(L)/3.);
    l -= b;
    O *= 0.;
    if ( l > 0. )
        P += l*D,
        P.xy *= mat2(cos( (length(P.xy) + P.z) /2. -t/2. + vec4(0,33,11,0))), // rot(time/2.), twist(P, -.25);
        P = vec3( length(P.xy)*4. + t+t , atan(P.y,P.x) * 25. , t * .05 ),
        f = fract(P), P -= f,
        f = f*f*(3.-f-f),                      
     // U = textureLod( iChannel0, ((P.xy+vec2(37,17)*P.z) + f.xy+.5)/256., 0. ).yx, // old fashion t3D emulation
     // c =  mix( U.x, U.y, f.z ) -.3 ,                   //  noise( P );
        c =  texture( iChannel1, (P+f+.5)/32. ).x -.3,
        O += smoothstep(.75, -.75, c/fwidth(c) );
}

