float rayMarch(in vec3 ro, in vec3 rd)//
{
    float t=TMIN;
    for(int i=0;i<RAYMARCH_TIME &&t<TMAX;i++)
    {
        vec3 p=ro+t*rd;
        float d=sdfSphere(p);
        if(d<PRECISION)
        {
            //return t;
            break;
        }
        t += d;
    }
    return t;
}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal(in vec3 p)//
{
    const float h=0.0001;;
    const vec2 k= vec2 (1,-1);
   return normalize(k.xyy * sdfSphere(p + k.xyy * h) +
        k.yyx * sdfSphere(p + k.yyx * h) +
        k.yxy * sdfSphere(p + k.yxy * h) +
        k.xxx * sdfSphere(p + k.xxx * h));
}

mat3 setCamera(vec3 ta,vec3 ro,float cr)//
{
    vec3 z=normalize(ta-ro);
    vec3 cp=vec3(sin(cr) , cos(cr),0.);
    vec3 x= normalize(cross(z,cp));
    vec3 y=cross(x,z);
    return mat3(x,y,z);

}

vec3 render(vec2 uv )
{
    vec3 color =vec3(.05);
    vec3 ro =vec3 (2.*cos(iTime),1.,2.*sin(iTime));//
    if(iMouse.z>0.001)//
    {
        float theta=iMouse.x/iResolution.x*2.*PI;
        ro=vec3(2.*cos(theta),1.+0.2*cos(iTime),2.*sin(theta));
    }
    vec3 ta=vec3(0.);
    mat3 cam=setCamera(ta,ro,0.);
    vec3 rd =normalize(cam*vec3 (uv,1.));
    float t=rayMarch(ro,rd);
    if(t<TMAX)
    {
        vec3 p=ro+t*rd;
        vec3 n= calcNormal(p);
        vec3 light =vec3 (2.*cos(iTime),2.,2.*sin(iTime+2.));//
        //vec3 light =vec3 (2.,2.,2.);
        float dif=clamp(dot(normalize(light-p),n),0.,1.);

        float amd=0.5+0.5*dot(n,vec3(0.,1.,0));//

        color=amd*vec3(0.05)+dif*vec3(.6);

    }
    return sqrt(color);//

}

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    vec3 color=vec3(0.);
    for(int m =0;m<AA;m++)
    {
        for(int n=0; n<AA;n++)
        {
            vec2 offset=2.*(vec2(float(m),float(n))/float(AA)-0.5);
            vec2 uv = fixUV(fragCoord+offset);
            color += render(uv);
        }
    }
    fragColor=vec4(color/float(AA*AA),1.);
}

#define SS(a,b,c) smoothstep(a-b, a+b, c)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 andy = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 brian = vec3(0.);
    brian = vec3(andy.x, andy.y, 1.);
    brian = 0.5 + 0.5 * cos(andy.xyx + iTime + vec3(0,2,4));
    float christy = 0.; // sin(andy.x * 200. + iTime * sin(andy.y)) * 0.04;
    christy += sin(andy.x*50. + iTime*10.)*0.01;

    // float greg = smoothstep( 0.001, 0.05 + christy, abs (andy.y-0.5)); 
    float greg = SS(christy + 0.05, 0.1, abs(andy.y-0.5+sin(iTime*2. + andy.x*2.)*0.2) );

    brian = mix (brian, vec3(sin(iTime*0.3 + andy.x)), 1.-greg);

    // Output to screen
    fragColor = vec4(brian,1);

}

