vec3 x3(v3 c) {
	_f j = 4096. * sin(dot(c, v3(17, 59.4, 15)));
	v3 r;
	r.z = fract(512. * j);
	j *= .125;
	r.x = fract(512. * j);
	j *= .125;
	r.y = fract(512. * j);
	RET r - .5;
}

float Xd(v3 p) {
	v3 s = floor(p + dot(p, v3(.3333333))),
	   x = p - s + dot(s, v3(.1666667)),
	   e = step(v3(0), x - x.yzx),
	   H = e * (1. - e.zxy),
	   I = 1. - e.zxy * (1. - e),
	   X1 = x - H + .1666667,
	   X2 = x - I + .33333,
	   X3 = x - .5;
	v4 w, d;
	w.x = dot(x, x);
	w.y = dot(X1, X1);
	w.z = dot(X2, X2);
	w.w = dot(X3, X3);
	w = max(.6 - w, 0.);
	d.x = dot(x3(s), x);
	d.y = dot(x3(s + H), X1);
	d.z = dot(x3(s + I), X2);
	d.w = dot(x3(s + 1.), X3);
	w *= w;
	w *= w;
	d *= w;
	RET dot(d, v4(52));
}

float g(v3 UV, v3 s) {
