	s = sin(a.y);
	p.x = RV.x * c + RV.z * s;
	p.y = RV.y;
	p.z = -RV.x * s + RV.z * c;
	c = cos(a.z);
	s = sin(a.z);
	RV.x = p.x * c + p.y * s;
	RV.y = -p.x * s + p.y * c;
	RV.z = p.z;
