float we(_f x) { RET .5 - .5 * cos(6.28319 * x); }

float U(_f x, _f y, _f z) {
	_f XY = min(x + y, 2. - x - y);
	RET min(XY + z, 2. - XY - z);
}

float S(_f a, _f b, _f k) {
	_f h = max(k - abs(a - b), 0.) / k;
	RET min(a, b) - h * h * k * .25;
}

vec4 R(v4 a, v4 b) {
	_f h,
	   k = 1.;
	k = max(k, .001);
	h = S(a.w, b.w, k);
	v2 n = NM(v2(S(a.w + .001, b.w, k) - S(a.w - .001, b.w, k), S(a.w, b.w + .001, k) - S(a.w, b.w - .001, k)));
	RET v4(mix(a.xyz, b.xyz, atan(abs(n.y), abs(n.x)) / 1.5708), h);
