vec4 Q(v4 J) {
	const v4 K = v4(0, 0, 0, 9999999),
	         L = v4(0, 0, 0, 9999999),
	         M = v4(0, 0, 0, 9999999);
	v4 t = (J.w < K.w) ? v4(J) : v4(K);
	t = (t.w < K.w) ? t : v4(K);
	t = (t.w < L.w) ? t : v4(L);
	RET (t.w < M.w) ? t : v4(M);
}

float t0(v3 v, _f _) {
