	if (UV.w > 13.5) RET v4(0. * n0(v4(UV.xyz, 14), _), sf);
	if (UV.w > 12.5) RET v4(0. * k0(v4(UV.xyz, 13), _), sf);
	if (UV.w > 4.5) RET v4(0. * o0(v4(UV.xyz, 5), _), 0, 0, sf);
