vec4 P9(v4 UV, _f _) {
	UV.xyz = P(UV.xyz - v3(0), v3(0));
	_f sf = e0(UV.xyz, _);
	if (UV.w > 18.5) RET v4(0. * od(v4(UV.xyz, 19), _), 0, 0, sf);
	if (UV.w > 14.5) RET v4(0. * Z(v4(UV.xyz, 15), _), 0, 0, sf);
	if (UV.w > 13.5) RET v4(0. * c0(v4(UV.xyz, 14), _), sf);
	if (UV.w > 12.5) RET v4(0. * a0(v4(UV.xyz, 13), _), sf);
