vec4 Y(v4 p, _f _) { RET Q(R(P5(p, _), P9(p, _))); }

vec3 E(v3 p, _f PO) {
	p = p.yzx;
	RET pow(texture(iChannel0, p).xyz, v3(2.2)) + pow(texture(iChannel0, p).xxx, v3(16)) * PO;
}

vec3 W(v3 p, _f _) {
	const v3 e = v3(.001, -.001, 0);
	_f V1 = Y(v4(p + e.xyy, 0), _).w,
	   V2 = Y(v4(p + e.yyx, 0), _).w,
	   V3 = Y(v4(p + e.yxy, 0), _).w,
	   V4 = Y(v4(p + e.xxx, 0), _).w;
	RET NM(v3(V4 + V1 - V3 - V2, V3 + V4 - V1 - V2, V2 + V4 - V3 - V1));
}

void O(inout float d, inout vec3 p, _f D, v3 RO, v3 RD, _f _) {
	for (int i = 0; i < 500; i++) {
		p = RO + RD * d;
		D = Y(v4(p, 0), _).w;
		d += D;
		if (d > 50. || abs(D) < 1e-4) break;
	}
}

float i(v3 ps, v3 V, _f _) {
	_f u0 = 0.,
	   sa = 1.;
	for (int i = 0; i < 5; i++) {
		_f h = .001 + .25 * _f(i) / 4.,
		   d = Y(v4(ps + h * V, 0), _).w;
		u0 += (h - d) * sa;
		sa *= .98;
	}

	RET _c(1. - 1.6 * u0, 0., 1.);
}

float m(v3 RO, v3 RD, _f _) {
	_f rs = 1.,
	   t = .05;
	for (int i = 0; i < 32; i++) {
		_f h = Y(v4(RO + RD * t, 0), _).w;
		rs = min(rs, 10. * h / t);
		t += h;
		if (rs < 1e-4 || t > 5.) break;
	}

	RET _c(rs, 0., 1.);
}

vec3 r1(v2 UV) {
	_f OR, os,
	   _ = 0.,
	   d = 0.,
	   D = 0.;
	UV -= .5;
	v3 or, N,
	   u = v3(0),
	   o = v3(sin(iT * .1) * 5., 4.077, cos(iT * .13) * 5.) * 1.153,
	   ry = NM(v3(0, .289, 0) - o),
	   A = NM(cross(v3(0, 1, 0), ry)),
	   RD = NM(ry * 1.5 + A * UV.x + NM(cross(A, ry)) * UV.y),
	   RO = o,
	   p = v3(0);
	O(d, p, D, RO, RD, _);
	or = Y(v4(p, 1), _).xyz;
	OR = Y(v4(p, 4), _).x * .2;
	os = Y(v4(p, 5), _).x;
	N = v3(22e-5, 1, 22e-5);
	if (d < 50.) {
		v3 rf, OF,
		   n = W(p, _);
		_f l = _c(dot(-N, -n), 0., 1.);
		rf = NM(reflect(RD, -n));
		u = min(v3(max(mix(1., m(p, N, _), 1.), .07)), max(l, .07)) * max(mix(1., i(p, n, _), 1.), .07) * or + 4. * pow(_c(dot(rf, N), 0., 1.), pow(256., 1. - os)) * OR;
		d = .01;
		O(d, p, D, p, rf, _);
		OF = v3(0);
		if (d < 50.) {
			OF = Y(v4(p, 1), _).xyz;
			n = W(p, _);
			l = _c(dot(-N, -n), 0., 1.);
			OF = max(l, .07) * OF;
		}
		else OF = E(rf.xzy, 6.);

		u = mix(u, OF, OR);
	}
	else u = E(RD.xzy, 0.);

	RET pow(u, v3(.50942));
}

void mainImage(out vec4 F, v2 G) {
	_f T = min(iR.x, iR.y);
	F = v4(r1(v2(0, 1) + v2(1, -1) * (G - .5 * (iR.xy - v2(T))) / T), 1);
}

