void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);

    Font f;
    f.texSize = iChannelResolution[3].xy;
    f.charSize = iChannelResolution[3].xy / vec2(16, 16);

    const vec2 charScale = vec2(20, 20);
    TextCursor t;
    t.startPos = vec2(0, iResolution.y);
    t.coord = ivec2(0);
    t.font = f;
    t.color = vec4(1,1,0,1);
    t.charSize = vec2(1,1) * charScale;
    t.charSpace = vec2(0.475, 0.8) * charScale;
    t.pageWidth = 40;

    declString(s, 11, (cH, cE, cL, cL, cO, cSp, cW, cO, cR, cL, cD));  

    for(int i = 0; i < 10; i++)
    {
        pline(s, t);
    }

    pnl(t);

    for(int i = 0; i < 10; i++)
    {
        pstr(s, t);
        pch(cSp, t);
    }

    t.color = vec4(0,1,1,1);

    for(int i = 0; i < 10; i++)
    {
        pch(Digits[i], t);
    }

    pch(cNl, t);

    for(int i = 0; i < 26; i++)
    {
        pch(Uppercase[i], t);
        pch(Lowercase[i], t);
    }

    t.color = vec4(1,1,1,1);

    pnl(t);
    pint(int(iTime * 1000.0), t);
    pnl(t);
    pfloat(iTime, t);
}

