// ---------------------------------------------------------------------------------------
// Computes particle positions and neighbors
// ---------------------------------------------------------------------------------------

const vec2 GRAVITY = vec2(0., -.00002);
const float COLLISION_STIFFNESS = .05;
const float CONSTRAINT_STIFFNESS = .3;
const float BEND_RESISTANCE = .1;
const float MAX_VEL = .003;
const float DAMPING = .97;
const float BOUNDARY_DIST = .004;

void sort0(inout ivec4 bestIds, inout vec4 bestDists, int id, int searchId, int dataType, in vec2 myPos);

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
    ivec2 iFragCoord = ivec2(fragCoord);

    //we only simulate MAX_PARTICLES amount of particles
    int index = iFragCoord.x + iFragCoord.y*int(iResolution.x);
    int id = index / NUM_PARTICLE_DATA_TYPES;
    int dataType = index - id * NUM_PARTICLE_DATA_TYPES;
    if(id>=MAX_PARTICLES) return;

    fxParticle data = fxGetParticle(id);
    fxState state = fxGetState();

    if (dataType >= POS)
    {
        if (iFrame == 0 || state.resolution < 0.)
        {
            // init particles
            int wormId = id / PARTICLES_PER_WORM;
            vec3 h1 = hash3(uvec3(wormId) * uvec3(3, 6, 9));

            data.pos = .9*VIEW_RESTRICT * vec2(1, 2) * h1.xy * iResolution.x / iResolution.y + vec2(0, 0);
            data.pos += rot2(h1.z) * vec2(1, 0) * PARTICLE_REPEL_SIZE * float(id % PARTICLES_PER_WORM);
            data.prev = data.pos;
        }
        else
        {
            // handle collisions with neighbors
            for(int i = 0; i < 4; i++)
            {
                ivec4 neighbors = data.neighbors[i];
                for (int j = 0; j < 4; ++j)
                {
                    int cid = neighbors[j];
                    if(cid==id || cid == id - 1 || cid == id + 1 || cid==-1 || cid == 0 || cid >= MAX_PARTICLES) continue;

                    fxParticle n = fxGetParticle(cid);

                    vec2 deltaPos = data.pos - n.pos;
                    float dist = length(deltaPos) + 0.0001;
                    vec2 dir = deltaPos / dist;

                    if (dist < PARTICLE_REPEL_SIZE)
                    {
                        data.pos = mix(data.pos, n.pos + PARTICLE_REPEL_SIZE * dir, COLLISION_STIFFNESS);
                    }
                }
            }       

            bool offScreen = data.pos.y < -.5;

            // handle internal worm constraints
            int wormId = id / PARTICLES_PER_WORM;
            int beginP = wormId * PARTICLES_PER_WORM;
            int endP = min(MAX_PARTICLES - 1, beginP + PARTICLES_PER_WORM) - 1;

            for (int i = beginP; i < endP; ++i)
            {
                if (i != id && i < MAX_PARTICLES)// && abs(i - id) <= 1)
                {
                    float cLen = abs(float(i - id)) * PARTICLE_REPEL_SIZE * .5;
                    fxParticle w = fxGetParticle(i);

                    if (w.pos.y > -.5) offScreen = false;

                    float f = 1. / square(abs(float(i - id)));
                    if (distance(w.pos, data.pos) < cLen)
                    {
                        f *= BEND_RESISTANCE;
                    }

                    data.pos = mix(data.pos, w.pos + cLen * normalize(data.pos - w.pos), CONSTRAINT_STIFFNESS * f);
                }
            }

            // handle teleporting back to top after falling off the bottom (has to be entire worms at once)
            if (offScreen)
            {
                float h = hash3(uvec3(wormId, wormId * iFrame, iFrame)).x;
                data.pos.x = VIEW_RESTRICT * h * iResolution.x / iResolution.y;
                data.pos.y += 2.5;
                data.prev = data.pos;
            }

            // handle mouse input
            if (iMouse.z > 0.)
            {
                vec2 m = VIEW_RESTRICT * (iMouse.xy) / iResolution.y;
                const float MOUSE_RANGE = .05;
                if (distance(m, data.pos) < MOUSE_RANGE)
                {
                    const float MOUSE_STRENGTH = .01;
                    data.pos = mix(data.pos, m, MOUSE_STRENGTH);
                }
            }

            // handle boundary
            float distToScene = distanceFromWalls(data.pos, iResolution);
            if (distToScene < BOUNDARY_DIST)
            {
                vec2 distNormal = getNormalFromWalls(data.pos, iResolution);
                data.pos -= 1.0 * distNormal * (distToScene - BOUNDARY_DIST);
            }

            // handle position integration
            vec2 prev = data.prev;
            vec2 deltaPos = prev - data.pos;
            float vel2 = length2(deltaPos);
            if (vel2 > MAX_VEL * MAX_VEL)
            {
                prev = data.pos + inversesqrt(vel2) * deltaPos * MAX_VEL;
            }
            data.prev = data.pos;
            data.pos = data.pos + GRAVITY + (data.pos - prev) * DAMPING;
        }
    }
    else
    {
        // Nearest particle sort inspired by michael0884's Super SPH: https://www.shadertoy.com/view/tdXBRf
        //sort neighbors and neighbor neighbors
        ivec4 closest = fxGetClosest(ivec2(world2screen(data.pos * vec2(iResolution.y / iResolution.x, 1.0))));
        ivec4 bestIds = ivec4(-1);
        vec4 bestDists = vec4(1e6);

        for (int i = 0; i < 4; ++i)
        {
            sort0(bestIds, bestDists, id, int(data.neighbors[0][i]), dataType, data.pos);  //sort this
            sort0(bestIds, bestDists, id, int(data.neighbors[1][i]), dataType, data.pos);  //sort this
            sort0(bestIds, bestDists, id, int(data.neighbors[2][i]), dataType, data.pos);  //sort this
            sort0(bestIds, bestDists, id, int(data.neighbors[3][i]), dataType, data.pos);  //sort this
            sort0(bestIds, bestDists, id, int(closest[i]), dataType, data.pos);  //sort this
        }

        int searchIterations = 2;
        for(int k = 0; k < searchIterations; k++)
        {
            //random hash. We should make sure that two pixels in the same frame never make the same hash!
            float h = hash(
                iFragCoord.x + 
                iFragCoord.y*int(iResolution.x) + 
                iFrame*int(iResolution.x*iResolution.y) +
                k);
            //pick random id of particle
            int p = int(mod(h*34534.0, float(MAX_PARTICLES)));
            sort0(bestIds, bestDists, id, int(p), dataType, data.pos);  //sort this
        }
        fragColor = vec4(bestIds);
        return;
    }

    fragColor = fxSaveParticle(data, dataType);
}

bool iscoincidence(in ivec4 bestIds, int currentId, int id)
{
    return id < 0 || id >= MAX_PARTICLES ||
      	id == currentId ||
        any(equal(bestIds,ivec4(id)));
}

void sort0(inout ivec4 bestIds, inout vec4 bestDists, int currentId, int searchId, int dataType, in vec2 myPos)
{
    if(iscoincidence(bestIds, currentId, searchId)) return; //particle already sorted

    vec2 nbX = fxGetParticleData(searchId, POS).xy; 

    vec2 dx = nbX - myPos;
    int dir = int(2.*(atan(dx.y, dx.x)+PI)/PI); 

    if(dir != dataType) return; //not in this sector

    float t = length2(dx);

    insertion_sort(bestIds, bestDists, searchId, t);
}

