// ---------------------------------------------------------------------------------------
// Renders particles: rgb contains color, alpha contains density
// ---------------------------------------------------------------------------------------

const vec3 lightDir = normalize(vec3(1,-1,1));

const float PARTICLE_SIZE = .005;

const vec3[5] MAT_COLORS = vec3[](vec3(0., 1., 0.),
                                  vec3(1., .7, 0.),
                                  vec3(1., 0., 0.),
                                  vec3(1., 1., 0.),
                                  vec3(.9, .9, .6));


void renderParticle(in fxParticle p, int id, in vec2 fragCoord, inout vec4 fragColor)
{   
    vec2 closest;
    p.pos.x *= iResolution.y / iResolution.x;
    p.prev.x *= iResolution.y / iResolution.x;
    float dist = sqrt(fxLinePointDist2(p.pos, p.prev, fragCoord, iResolution.x / iResolution.y));

    int material = int(id * 4 / PARTICLES_PER_WORM) % 5;
    fragColor.a = max(fragColor.a, mix(fragColor.a, 1., 1.-smoothstep(PARTICLE_SIZE * .15, PARTICLE_SIZE * 2., dist)));
    if (dist < PARTICLE_SIZE)
    {
        if (fragColor.xyz == vec3(0))
            fragColor.xyz = MAT_COLORS[material];
        else
            fragColor.xyz = mix(fragColor.xyz, MAT_COLORS[material], .5);
    }

    // Render neighbor lines
    if (keyDown(KEY_SHIFT))
    {
        for(int i = 0; i < 4; i++){
            ivec4 neighbors = p.neighbors[i];
            for (int j = 0; j < 4; ++j)
            {
                int cid = neighbors[j];
                if(cid==-1 || cid >= MAX_PARTICLES || cid == 0) continue;

                vec2 otherPos = fxGetParticleData(cid, POS).xy;
                otherPos.x *= iResolution.y / iResolution.x;

                float distToLin = fxLinePointDist2(p.pos, p.pos + 0.5 * (otherPos - p.pos), fragCoord, iResolution.x / iResolution.y);
                fragColor.xyz += iResolution.x* max(0.0, 0.001 - sqrt(distToLin)) / (0.0004);
            }
        }
    }
}

void mainImage( out vec4 fragColor, vec2 fragCoord )
{    
    fragColor = vec4(0);

  	vec2 p = fragCoord/iResolution.xy;

    //get the id's of the 4 particles that (should be) closest.
    //the 4 ids are stored in .x, .y, .z, .w
    ivec4 ids = fxGetClosest(ivec2(fragCoord));

    //draw the particles
    for(int i = 0; i < 4; i++)
    {
        //get the particles position
        int id = ids[i];
        fxParticle particle = fxGetParticle(id);

        renderParticle(particle, id, p, fragColor);
    }
}

