vec4 Laplacian(ivec2 uv, sampler2D Chan){

    ivec2 dx = ivec2(1, 0);
    ivec2 dy = ivec2(0, 1);

    vec4 L = vec4(0.);

    L += texelFetch(Chan, uv+dx, 0);
    L += texelFetch(Chan, uv+dy, 0);
    L += texelFetch(Chan, uv-dx, 0);
    L += texelFetch(Chan, uv-dy, 0);

    L += .5*texelFetch(Chan, uv+dx+dy, 0);
    L += .5*texelFetch(Chan, uv+dy-dx, 0);
    L += .5*texelFetch(Chan, uv-dx-dy, 0);
    L += .5*texelFetch(Chan, uv-dy+dx, 0);

    L -= texelFetch(Chan, uv, 0)*6.;

    return L;

}

