        //vec2 dir = vec2(sin(iTime + 1.0) * .5,rstandard);

        float d = length(uv - i* tan(t));

        float brightness = mix( 0.005, .0001, smoothstep(0.005, 0.0, t) );
        brightness *= sin(t*20.0 + i)* .5 +.5;
        brightness*= smoothstep(1.,.5,r);
        col += brightness/d * color;
    }

    col*= 2.0;
    fragColor = vec4(col,1.0);     // Output to screen
