vec3 white = vec3(.941, .965, .941);


mat2 r2d(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, s, -s, c);
}

float ex(float t) {
    if (t == 0. || t == 1.) return t;
    return (t *= 2.) < 1. ? .5 * exp2(10. * (t - 1.)) : .5 * (-exp2(-10. * (t - 1.)) + 2.);
}

float repeat(float pos, float dist) {
    return mod(pos - dist * .5, dist) - dist * .5;
}

void repeat_angle(inout vec2 pos, float m) {
    float a = repeat(atan(pos.y, pos.x), m);
    pos = vec2(cos(a), sin(a)) * length(pos);
}

void mirror(inout vec2 pos, vec2 dist) {
    pos = abs(pos) - dist;
    if (pos.y > pos.x)
        pos = pos.yx;
}

float sdf_cross(vec3 pos, float dist) {
    pos = abs(pos);
    pos = max(pos, pos.yzx);
    return min(pos.x, min(pos.y, pos.z)) - dist;
}

float sdf_cylinder(vec2 pos, float dist) {
    return length(pos) - dist;
}

float shape1(vec3 p) {
    // probably overkill for what we get visually,
    // but it's the result of iterative experimentation & exploration (wont rewrite)
    repeat_angle(p.xy, tau / 3.);
    p.z = repeat(p.z, 1.);
    mirror(p.xz, vec2(1, 4));
    p.x = abs(p.x) - 14.;
    mirror(p.xz, vec2(1, 3));
    mirror(p.xy, vec2(8));
    return sdf_cross(p, .1);
}

float shape2(vec3 p) {
    p.xy *= r2d(p.z * .02);
    mirror(p.xy, vec2(11, 12));
    repeat_angle(p.xy, tau / 7.);
    mirror(p.xy, vec2(12. * pex(time * .5), 1.6));
    p.x = abs(p.x) - 4. + pex(time * .5);
    p.xy *= r2d(p.z * .2 + time);
    p.x = abs(p.x) - .3;
    return sdf_cylinder(p.xy, .15);
}

float de(vec3 p) {
    p.xy *= r2d(time * .1);
    return min(shape1(p), shape2(p));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    vec3 ray_origin = vec3(0, 0, time * 12.);
    vec3 ray_direction = normalize(vec3(uv, 1)), p;
    vec3 pos;

    float iterations = 0.;
    float tdist = 0.; // total distance from the ray origin to the point
    // Sphere-tracing
    for (float i = 0.; i < 1.; i += .01) {// 100 iterations
        iterations = i;
        pos = ray_origin + ray_direction * tdist;
        float dist = de(pos); // signed distance field
        if (dist < .01)
            break;
        tdist += dist;
    }

    vec3 color = mix(white, black, iterations);
    fragColor = vec4(color, 1);
}

/*
    Equirectangular Projection & Inverse
    by TechDev

    CC0 1.0 Universal (CC0 1.0) Public Domain Dedication
*/

#define rcp(x) (1.0 / (x))

const float TAU = radians(360.0);
const float INV_TAU = rcp(TAU);

const float PI = radians(180.0);
const float INV_PI = rcp(PI);

vec3 EquirectangularToView(vec2 coord)
{
    coord = coord * TAU - PI; 

    float theta  = coord.x;
    float phi    = coord.y * 0.5;
    float cosPhi = cos(phi);

    return vec3(
        cosPhi * cos(theta),
        sin(phi)           ,
        cosPhi * sin(theta)
    );
}

vec2 ViewToEquirectangular(vec3 view)
{
    return vec2(
        atan(view.z, view.x) * INV_TAU + 0.5,
        1.0 - acos(view.y) * INV_PI
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord.xy / iResolution.xy; 
    vec3 cubeMapDir = EquirectangularToView(coord);

    fragColor = texture(iChannel0, EquirectangularToView(coord));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       float t,i,d=1.,s=iTime*.6;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));                                                                                                                                                                                                                                                                                                                                                                                                                                                         // https://www.shadertoy.com/view/ddlSRn

    // Output to screen 
    fragColor = vec4(col,1.0);                                                                                                                                                                                                                                                                             for(vec2 r=iResolution.xy;d>.001&&++i<1e2;t+=d=(length(fract(vec3((fragCoord*2.-r)/r.y/8.*t*t*(sin(s)+1.2),t+s))-.5)-sin(s+t*5.)*.15-.2)/3.)fragColor=mix(fragColor,(cos(t+vec4(0,1,2,0))+i*.03)/exp(t*.2),smoothstep(-1.,1.,sin(s+4.3)*2.));
}

