#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .0005
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(1.0),b,d)
#define B(p,s) max(abs(p).x-s.x,abs(p).y-s.y)
#define Tri(p,s,a) max(-dot(p,vec2(cos(-a),sin(-a))),max(dot(p,vec2(cos(a),sin(a))),max(abs(p).x-s.x,abs(p).y-s.y)))
#define DF(a,b) length(a) * cos( mod( atan(a.y,a.x)+6.28/(b*8.0), 6.28/((b*8.0)*0.5))+(b-1.)*6.28/(b*8.0) + vec2(0,11) )
#define ZERO (min(iFrame,0))
#define EDGE_WIDTH 0.01
#define MATERIAL0 0
#define MATERIAL1 1

float edge;

float random (vec2 p) {
    return fract(sin(dot(p.xy, vec2(12.9898,78.233)))* 43758.5453123);
}

vec2 combine(vec2 val1, vec2 val2 ){
    return (val1.x < val2.x)?val1:val2;
}

// thx iq! https://iquilezles.org/articles/distfunctions/
float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

// thx iq! https://iquilezles.org/articles/distfunctions/
float sdBox( vec3 p, vec3 b )
{
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

// thx iq! https://iquilezles.org/articles/distfunctions/
float sdCappedCylinderX( vec3 p, float h, float r )
{
    vec2 d = abs(vec2(length(p.yz),p.x)) - vec2(h,r);
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

// thx iq! https://iquilezles.org/articles/distfunctions/
float sdCappedCylinderZ( vec3 p, float h, float r )
{
    vec2 d = abs(vec2(length(p.xy),p.z)) - vec2(h,r);
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

// thx iq! https://iquilezles.org/articles/distfunctions/
// tweaked as the center aligned horizontal capsule. 
float sdHorizontalCapsule( vec3 p, float w, float r )
{
      p.x-= clamp( p.x, -w*0.5, w*0.5 );
      return length( p ) - r;
}

float cubicInOut(float t) {
  return t < 0.5
    ? 4.0 * t * t * t
    : 0.5 * pow(2.0 * t - 2.0, 3.0) + 1.0;
}

float getTime(float t, float duration){
    return clamp(t,0.0,duration)/duration;
}

float board(vec3 p){
    vec3 prevP = p;
    float thick = 0.01;
    float r = 0.37;
    float h = 0.75;

    float d = sdBox(p,vec3(r,h,thick));
    p.y=abs(p.y)-h;
    p.yz*= Rot(radians(-10.));
    float d2 = sdCappedCylinderZ(p,r,thick);
    d2 = max(-p.y,d2);
    d = min(d,d2);
    return d;
}

float trackWithWheel(vec3 p){
    vec3 prevP = p;

    float d = sdHorizontalCapsule(p,0.63,0.035);
    p.y+=0.03;
    float d2 = sdCappedCylinderZ(p,0.15,0.03);
    d2 = max(p.y,d2);
    d = opSmoothUnion(d,d2,0.05);
    p.y+=0.08;
    d2 = sdCappedCylinderZ(p,0.05,0.1);
    d = max(-d2,d);

    p = prevP;
    p.z-=0.12;
    p.y+=0.075;
    d2 = sdBox(p,vec3(0.12,0.15,0.015));
    d = min(d,d2);
    p.z+=0.025;
    d2 = sdBox(p,vec3(0.05,0.15,0.05));

    float a = radians(-45.);
    p.y=abs(p.y)-0.13;
    d2 = max(dot(p.yz,vec2(cos(a),sin(a))),d2);
    d = min(d,d2);

    p = prevP;
    p.z-=0.02;
    p.y+=0.1;
    d2 = sdCappedCylinderZ(p,0.04,0.05);
    d = min(d,d2);

    p = prevP;
    p.x = abs(p.x)-0.3;
    d2 = sdCappedCylinderX(p,0.08,0.04)-0.03;
    d2 = max(-sdCappedCylinderX(p,0.04,0.2),d2);
    d = min(d,d2);

    p = prevP;
    p.z-=0.12;
    p.y+=0.07;
    p.x = abs(p.x)-0.085;
    p.y = abs(p.y)-0.11;
    d2 = sdCappedCylinderZ(p,0.01,0.04);
    d = min(d,d2);

    return d;
}

float arrowFractal(vec2 p, int dir, float num){
        float d = 10.;

        float dist = 0.07;
        if(dir == 0){
            p.y = abs(p.y)-0.5;
            p*=Rot(radians(90.0));
        } else {
            p.x = abs(p.x)-1.5;
            dist = 0.06;
        }

        for(float i = 0.; i<num; i++){
            p*=Rot(radians(i*30.0+iTime*sin(i)*20.));
            p*=0.9-i*0.1;
            p = abs(p)-dist;
            p-=0.025+i*0.01;

            d = Tri(p,vec2(0.1),radians(45.));
            p.y+=0.05;
            float d2 = Tri(p,vec2(0.1),radians(45.));
            d = max(-d2,d);

            float d3 = B(p-vec2(0.,-0.03),vec2(0.017,0.06));
            d = abs(min(d,d3))-0.005;

            if(dir == 1){
                d = abs(d)-0.0005;
            }
        }
        return d;
}

vec3 skateboardAnim(vec3 p){

    float rotX = 0.;
    float rotY = 0.;

    float frame = mod(iTime,15.0);
    float time = frame;

    if(frame>=2.5 && frame<5.){
        time = getTime(time-2.5,0.6);

        rotX = cubicInOut(time)*90.0;
    } else if(frame>=5. && frame<7.5){
        time = getTime(time-5.0,0.6);

        rotX = 90.+cubicInOut(time)*90.0;
    } else if(frame>=7.5 && frame<10.){
        time = getTime(time-7.5,0.6);

        rotX = 180.+cubicInOut(time)*180.0;
    } else if(frame>=10. && frame<12.5){
        time = getTime(time-10.,0.6);

        rotY = cubicInOut(time)*180.0;
    } else if(frame>=12.5 && frame<15.){
        time = getTime(time-12.5,0.6);

        rotY = 180.+cubicInOut(time)*180.0;
    }

    p.xz*=Rot(radians(rotX));
    p.yz*=Rot(radians(rotY));
    return p;
}

vec2 skateboard(vec3 p) {
     vec3 prevP = p;

     p = skateboardAnim(p);

     float d = board(p-vec3(0.0,0.0,0.07));
     float mask = board(prevP-vec3(0.0,0.0,0.07));

     p.y=abs(p.y)-0.7;
     float d2 = trackWithWheel(p-vec3(0.0,0.0,-0.07));

     p = prevP;
     float d3 = arrowFractal(p.xy,1,5.);
     d3 = max(abs(p.z)-0.001,d3);
     d3 = max(-(mask-0.13),d3);

     vec2 res = combine( combine(vec2(d,MATERIAL0),vec2(d2,MATERIAL1)),vec2(d3,MATERIAL1));

     return res;
}

vec2 GetDist(vec3 p) {
    vec3 prevP = p;

    vec2 res = skateboard(p);

    return res;
}

vec2 RayMarch(vec3 ro, vec3 rd, float side, int stepnum) {
    vec2 dO = vec2(0.0);

    float lastDistEval = 10.; 
    float dist;
    for(int i=0; i<stepnum; i++) {
        vec3 p = ro + rd*dO.x;
        vec2 dS = GetDist(p);
        dist = dS.x;
        dO.x += dS.x*side;
        dO.y = dS.y;

        if(stepnum == MAX_STEPS){
            if (lastDistEval < EDGE_WIDTH && dist > lastDistEval) {
                edge = 1.0;
            }
            if (dist < lastDistEval) lastDistEval = dist;
        }      

        if(dO.x>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);

    return normalize(n);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

// https://www.shadertoy.com/view/3lsSzf
float calcOcclusion( in vec3 pos, in vec3 nor )
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=ZERO; i<4; i++ )
    {
        float h = 0.01 + 0.15*float(i)/4.0;
        vec3 opos = pos + h*nor;
        float d = GetDist( opos ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 2.0*occ, 0.0, 1.0 );
}

vec3 diffuseMaterial(vec3 n, vec3 rd, vec3 p, vec3 col) {
    float occ = calcOcclusion(p,n);
    vec3 diffCol = vec3(0.0);
    vec3 lightDir = normalize(vec3(1,10,-10));
    float diff = clamp(dot(n,lightDir),0.0,1.0);
    float shadow = step(RayMarch(p+n*0.3,lightDir,1.0, 15).x,0.9);
    float skyDiff = clamp(0.5+0.5*dot(n,vec3(0,1,0)),0.0,1.0);
    float bounceDiff = clamp(0.5+0.5*dot(n,vec3(0,-1,0)),0.0,1.0);
    diffCol = col*vec3(-0.5)*diff*shadow*occ;
    diffCol += col*vec3(1.0,1.0,0.9)*skyDiff*occ;
    diffCol += col*vec3(0.3,0.3,0.3)*bounceDiff*occ;
    diffCol += col*pow(max(dot(rd, reflect(lightDir, n)), 0.0), 60.)*occ; // spec

    return diffCol;
}

vec3 materials(int mat, vec3 n, vec3 rd, vec3 p, vec3 col){
    col = diffuseMaterial(n,rd,p,vec3(1.3));
    if(mat == MATERIAL0){
        p = skateboardAnim(p);
        float d = arrowFractal(p.xy,0,3.);
        col = mix(col,vec3(0.),S(d,0.0));
    }
    return col;
}

vec3 bg(vec2 p, vec3 col){
    vec2 prevP = p;
    p*=6.;
    p.y-=iTime*0.5;
    vec2 id = floor(p);
    vec2 grid = fract(p)-0.5;
    vec2 prevGrid = grid;

    float n = random(id);
    float speed = random(id*6.)*0.5;
    float display = 0.;
    if(n>=0.1 && n<0.2){
        grid*=Rot(radians(90.));
    } else if(n>=0.2 && n<0.3){
        grid*=Rot(radians(180.));
    } else if(n>=0.3 && n<0.4){
        grid*=Rot(radians(270.));
    } else if(n>=0.4){
        display =-1.;
        if(n>=0.8){
            grid*=Rot(radians(45.));
        }
        float d2 = abs(abs(B(grid,vec2(0.15)))-0.05)-0.01;
        col = mix(col,vec3(0.03),S(d2,0.0));
    }

    if(n<0.4){
        float d2 = abs(B(grid,vec2(0.45)))-0.01;

        float mask = B(grid,vec2(0.2,0.8));
        d2 = max(-mask,d2);
        mask = B(grid,vec2(0.8,0.2));
        d2 = max(-mask,d2);

        col = mix(col,vec3(0.03),S(d2,0.0));
    }

    grid.y-=iTime*(0.5+speed);
    grid.y-=0.45;
    grid.y=mod(grid.y,0.74)-0.37;

    grid.y-=0.18;

    float d = Tri(grid,vec2(0.36),radians(45.));
    grid.y+=0.18;
    float d2 = Tri(grid,vec2(0.36),radians(45.));
    d = max(-d2,d);

    float d3 = B(grid-vec2(0.,-0.16),vec2(0.07,0.2));
    d = abs(min(d,d3))-0.015;

    d = max(B(prevGrid,vec2(0.45)),d);
    col = mix(col,vec3(0.1),S(d,display));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 prevUV = uv;
    vec2 m =  iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 0, -2.5);
    if(iMouse.z>0.){
        ro.yz *= Rot(m.y*3.14+1.);
        ro.y = max(-0.9,ro.y);
        ro.xz *= Rot(-m.x*6.2831);
    } else {

        float rotX = 0.;
        float rotY = 0.;

        float frame = mod(iTime,20.);
        float time = frame;

        if(frame>=5.0 && frame<10.){
            time = getTime(time-5.0,1.);

            rotX = -cubicInOut(time)*20.0;
        } else if(frame>=10. && frame<15.){
            time = getTime(time-10.0,1.5);

            rotX = -20.+cubicInOut(time)*40.0;
        } else if(frame>=15.){
            time = getTime(time-15.0,1.);

            rotX = 20.-cubicInOut(time)*20.0;
        }

        ro.xz *= Rot(radians(rotX));
        ro.yz *= Rot(radians(rotY));
    }

    vec3 rd = R(uv, ro, vec3(0,0.0,0), 1.0);
    vec2 d = RayMarch(ro, rd, 1.,MAX_STEPS);
    vec3 col = vec3(.0);

    if(d.x<MAX_DIST) {
        vec3 p = ro + rd * d.x;
        vec3 n = GetNormal(p);
        int mat = int(d.y);
        col = materials(mat,n,rd,p,col);
        col = mix(col,vec3(0.0),edge);
    } else {
        col = bg(uv,col);
    }

    // gamma correction
    col = pow( col, vec3(0.9545) );    

    fragColor = vec4(sqrt(col),1.0);
}

#define PI 3.14159
#define S(a,b,t) smoothstep(a,b,t)

float box(vec2 uv, float width, float height, float stroke) {
    float blur = 0.01;
    // outer part
    float outer_horizontal = S(height/2., height/2. - blur, abs(uv.y));
    float outer_vertical = S(width/2., width/2. - blur, abs(uv.x));
    float outer = outer_horizontal * outer_vertical;
    // inner part
    float inner_horizontal = S(height/2.-stroke, height/2.-stroke - blur, abs(uv.y));
    float inner_vertical = S(width/2.-stroke, width/2.-stroke - blur, abs(uv.x));
    float inner = inner_horizontal * inner_vertical;
    // box
    float box = outer - inner;
    return box;
}

float makeBump(vec2 uv, float groundYoffset) {
    uv.x = fract(uv.x) - .5;
    float box = box(uv - vec2(0., groundYoffset), .3, .2, .02);
    return box;
}

