const float FIELD_OF_VIEW = 1.2;

// camera directions
const vec4 FWD_4 = vec4(1., 0., 0., 0.);
const vec4 UP_4 = vec4(0., 0., 1., 0.);

// raymarching parameters
const float GLOW_DIST = 0.05;
#ifdef HIGH_QUALITY

const float MAX_DIST = 300.;
#define MAX_ITERATIONS 64
const float STAR_SIZE = 0.15;
#else
#ifdef POTATO_QUALITY
const float MAX_DIST = 200.;
#define MAX_ITERATIONS 16
const float STAR_SIZE = 0.3;
#else
const float MAX_DIST = 300.;
#define MAX_ITERATIONS 32
const float STAR_SIZE = 0.2;
#endif
#endif

// cluster settings
const float CLUSTER_CELL_SIZE = 15.;
const float CLUSTER_RAND_VEL = float(STAR_VELOCITY);
const vec4 CLUSTER_FUTURE = vec4(0., 0., 0., 1.);
// Used to keep stars centered within their domains
const float CENTER_LIGHT_TIME_VEL = 1. / sqrt(1. + 0.5 * 0.5);

// projection operator
vec4 proj(vec4 u, vec4 v) {
    return (u * dot(u,v)) / dot(u, u);
}

// Calculate a vector orthogonal (but not orthonormal) to three other 4-vectors
vec4 cross4(vec4 u, vec4 v, vec4 t){
    return vec4(
        ( u.w * v.z - u.z * v.w) * t.y + (-u.w * v.y + u.y * v.w) * t.z + (-u.y * v.z + u.z * v.y) * t.w,
        (-u.w * v.z + u.z * v.w) * t.x + ( u.w * v.x - u.x * v.w) * t.z + ( u.x * v.z - u.z * v.x) * t.w,
        ( u.w * v.y - u.y * v.w) * t.x + (-u.w * v.x + u.x * v.w) * t.y + (-u.x * v.y + u.y * v.x) * t.w,
        ( u.y * v.z - u.z * v.y) * t.x + (-u.x * v.z + u.z * v.x) * t.y + ( u.x * v.y - u.y * v.x) * t.z

    );
}

// Returns a 4-d ray direction orthogonal to the time vector given a set of camera parameters.
vec4 camera_spacelike_dir(vec2 uv, vec4 cameraDirection, vec4 cameraUp, vec4 cameraFuture) {

	uv = uv * FIELD_OF_VIEW;

    // Up/fwd vec orthogonal to future
    vec4 fut = cameraFuture;
    vec4 up_vec = normalize(cameraUp - proj(fut, cameraUp));
    vec4 fwd_vec = normalize(cameraDirection - proj(fut, cameraDirection));
    // Right vec orthogonal to all other directions
    vec4 right_vec = normalize(cross4(up_vec, fwd_vec, fut));

    vec4 spacelike_dir = normalize(fwd_vec + uv.y * up_vec + uv.x * right_vec);

    return spacelike_dir;
}

// Returns 4-d ray direction given a spacelike direction and a velocity in the timelike direction.
vec4 camera(vec4 spacelike_dir, vec4 cameraFuture, float futureVel) {
    vec4 timelike_dir = cameraFuture * futureVel;

    return normalize(spacelike_dir + timelike_dir);
}

int hash(ivec3 co){
    return co.x + 123 * co.y + 12345 * co.z;
}


vec4 rand1_4(float i) {
    vec2 co = vec2(i, floor(i / iChannelResolution[0].x));
    return texture(iChannel0, co);
}

vec3 rand3_3(ivec3 i) {
    ivec2 co = ivec2(mod(float(i.x + 13 * i.z), iChannelResolution[0].x), mod(float(i.y + 29 * i.z), iChannelResolution[0].y));
    return texelFetch(iChannel0, co, 0).rgb;
}

vec3 rand3_1(ivec3 i) {
    ivec2 co = ivec2(mod(float(i.x + 13 * i.z), iChannelResolution[0].x), mod(float(i.y + 29 * i.z), iChannelResolution[0].y));
    return texelFetch(iChannel0, co, 0).rgb;
}

// Nebula density
float nebula(vec4 p) {
    // Component in space directions
    vec3 p_s = p.xyz;

    float n0 = texture(iChannel1, p_s * 0.005).r;
    float n1 = texture(iChannel1, p_s * 0.001).r;
    float n2 = texture(iChannel1, p_s * 0.0002).r;

    return clamp(n0 * n1  * n1 * n2 - 0.1, 0., 1.);
}

// 4D star cluster SDF
// SDF between a cluster and a dispersing line of light of different wavelengths.
// 'elongation' is a vector from the point at one end of the spectrum to the other.
float sdCluster (vec4 p, vec4 elongation, float l) {
    float dist = CLUSTER_CELL_SIZE;

    ivec3 p_idx = ivec3(floor(p.xyz / CLUSTER_CELL_SIZE));

    // Loop over adjacent cells
    #ifdef HIGH_QUALITY
    for(int x = -1; x <= 1; x++) {
        for(int y = -1; y <= 1; y++) {
            for(int z = -1; z <= 1; z++) {
    #else
    int x,y,z = 0;
    #endif
                ivec3 idx = p_idx + ivec3(x, y, z);

                vec3 star_future_3 = (rand3_3(idx) - vec3(0.5)) * CLUSTER_RAND_VEL;
                vec4 star_future = normalize(CLUSTER_FUTURE + vec4(star_future_3, 0.));

                vec3 star_pos_3 = rand3_3(idx + ivec3(17, 37, 49)) * CLUSTER_CELL_SIZE * 0.5 + CLUSTER_CELL_SIZE * 0.25;
                vec4 star_pos = vec4(star_pos_3 + vec3(idx) * CLUSTER_CELL_SIZE, CENTER_LIGHT_TIME_VEL * length(vec3(idx)));
                vec4 pa = p - star_pos;

                // Elongate along elongation vector
                vec4 orth_elongation = elongation - proj(star_future, elongation);
                float t2 = dot(pa, orth_elongation)/dot(orth_elongation, orth_elongation);
                pa = pa - clamp(t2, -1., 0.) * orth_elongation;

                // Elongate (infinitely) along star future
                float t1 = dot(pa, star_future)/dot(star_future, star_future);
                pa = pa - t1 * star_future;

                // Calculate distance to star
                dist = min(dist, length(pa));

    #ifdef HIGH_QUALITY
            }
        }
    }
    #endif
    return dist;
}

// Volumetric 4d raymarching
float raymarch4d(vec4 spacelike_rd, vec4 ro, vec4 future_vec, float lower_vel, float upper_vel) {

    vec4 lower_ray_dir = camera(spacelike_rd, future_vec, lower_vel);
    vec4 upper_ray_dir = camera(spacelike_rd, future_vec, upper_vel);
    vec4 elongation_vec = upper_ray_dir - lower_ray_dir;

    vec4 ray_pos = ro;
    float l = 0.;
    float d = 0.;
    float min_dist = MAX_DIST;
    //Accumulated density
    float a = 0.;
    // Raymarching loop
    for(int i = 0; i < MAX_ITERATIONS; i++){
        vec4 p = ray_pos + l * lower_ray_dir;
        d = sdCluster(ray_pos + l * lower_ray_dir, l * elongation_vec, l);
        a += min(1., exp2(STAR_SIZE - d / GLOW_DIST));
        l += d + STAR_SIZE * 0.5;
        if (l > MAX_DIST) {
            break;
        }
    }

    return a;

}

vec2 raymarch4dNebula(vec4 spacelike_rd, vec4 ro, vec4 future_vec, float lower_vel, float upper_vel) {

    vec4 lower_ray_dir = camera(spacelike_rd, future_vec, lower_vel);
    vec4 upper_ray_dir = camera(spacelike_rd, future_vec, upper_vel);
    vec4 elongation_vec = upper_ray_dir - lower_ray_dir;

    vec4 ray_pos = ro;
    float l = 0.;
    float d = 0.;
    float min_dist = MAX_DIST;
    //Accumulated density
    vec2 a = vec2(0.);
    // Raymarching loop
    for(int i = 0; i < MAX_ITERATIONS; i++){
        vec4 p = ray_pos + l * lower_ray_dir;
        d = sdCluster(ray_pos + l * lower_ray_dir, l * elongation_vec, l);
        float intensity = clamp(20. / (d), 5., 20.);
        a += vec2(nebula(p), nebula(p + vec4(1000., 0.,0.,0.))) * intensity;

        l += 5.;
        if (l > MAX_DIST) {

            break;
        }
    }
    return a;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 cam_uv = ((fragCoord/iResolution.xy) - vec2(0.5)) * vec2(iResolution.x / iResolution.y, 1.);

    // Calculate camera position & direction. The requires 3 orthogonal vectors to define in 4d.
    float orbit_rate = 0.05; // radians/sec

    vec2 m = iMouse.xy / iResolution.xy * 30. - vec2(15.);

    vec4 fwd = vec4(sin(iTime * orbit_rate), cos(iTime * orbit_rate), 0., 0.);
    #ifdef CAMERA_MOTION
    float vel = (0.1 - 0.1 * cos(iTime * 0.1));
    vec4 future = normalize(vec4(0., -0.5 * vel, 0., 1.));
    #else
    vec4 future = vec4(0.,0.,0.,1.);
    #endif
    vec4 camera_d = camera_spacelike_dir(cam_uv, fwd, UP_4, future);
    vec4 base_pos = 20. * (0.1 * iTime - sin(iTime * 0.1)) * vec4(0., 1.,0.,0.);
    #ifdef STARS_MOVE
    vec4 ray_pos = base_pos + fwd * m.x + UP_4 * m.y -future * iTime;
    #else
    vec4 ray_pos = base_pos + fwd * m.x + UP_4 * m.y;
    #endif


    // Raymarch 4 different wavelength/velocity ranges
    float r = raymarch4d(camera_d, ray_pos, future, 0.58, 0.65); // Red light is slowest (Pointed most towards the camera's future)
    float g = raymarch4d(camera_d, ray_pos, future, 0.51, 0.59);
    float b = raymarch4d(camera_d, ray_pos, future, 0.46, 0.54);
    float v = raymarch4d(camera_d, ray_pos, future, 0.44, 0.48); // Violet light is fastest

    vec2 n = raymarch4dNebula(camera_d, ray_pos, future, 0.44, 0.65) / float(MAX_ITERATIONS);

    // Wavelength range colors
    vec3 r_col = vec3(0.8, 0.1, 0.);
    vec3 g_col = vec3(0.1, 0.8, 0.);
    vec3 b_col = vec3(0., 0.2, 0.4);
    vec3 v_col = vec3(0.25, 0., 0.6);
    vec3 nebula_col1 = vec3(1., 0.5, 0.);
    vec3 nebula_col2 = vec3(0.5, 0.0, 1.);
    // Additively mix final color
    vec3 col = r * r_col + g * g_col + b * b_col + v * v_col + n.x * nebula_col1 + n.y * nebula_col2;

    fragColor = vec4(col, 1.);
}

